// BREAK example - exit loop early
for (int i = 1; i <= 10; i++)
{
    Console.WriteLine("Number: " + i);

    if (i == 5)
    {
        Console.WriteLine("Found 5! Exiting loop.");
        break;  // Exit the entire loop
    }
