String describeValue(Object value) {
  return switch (value) {
    // Match specific values
    0 => 'zero',
    1 => 'one',

    // Match types with binding
    int n when n < 0 => 'negative integer: $n',
    int n when n > 100 => 'large integer: $n',
    int n => 'integer: $n',

    // Match strings
    String s when s.isEmpty => 'empty string',
    String s when s.length > 10 => 'long string',
    String s => 'string: $s',

    // Match lists
    [] => 'empty list',
    [var single] => 'single element: $single',
    [var first, ...var rest] => 'list starting with $first',

    // Match records
    (int x, int y) => 'point at ($x, $y)',

    // Catch-all
    _ => 'something else: $value',
  };
}

void main() {
  print(describeValue(0));           // zero
  print(describeValue(-5));          // negative integer: -5
  print(describeValue(150));         // large integer: 150
  print(describeValue('hello'));     // string: hello
  print(describeValue([]));          // empty list
  print(describeValue([1, 2, 3]));   // list starting with 1
  print(describeValue((10, 20)));    // point at (10, 20)
}

void processData(Object data) {
  // If-case for conditional pattern matching
  if (data case int n when n > 0) {
    print('Positive integer: $n');
  }

  if (data case String s when s.startsWith('Hello')) {
    print('Greeting: $s');
  }

  // Match and extract from records
  if (data case (String name, int age) when age >= 18) {
    print('$name is an adult');
  }

  // Match list patterns
  if (data case [var first, _, var last]) {
    print('Three elements: first=$first, last=$last');
  }
}

void main() {
  processData(42);                    // Positive integer: 42
  processData('Hello, World!');       // Greeting: Hello, World!
  processData(('Alice', 25));         // Alice is an adult
  processData([1, 2, 3]);             // Three elements: first=1, last=3
  processData(-5);                    // (no output - doesn't match)
}

