  Map<String, dynamic>? userData;
  bool isLoading = true;
  String? errorMessage;
  bool isEditing = false;
  final nameController = TextEditingController();

  @override
  void initState() {
    super.initState();
    _loadUser();  // API call directly in widget!
  }

  // API logic mixed into the widget
  Future<void> _loadUser() async {
    setState(() => isLoading = true);
    try {
      // Direct HTTP call - no separation!
      final response = await http.get(
        Uri.parse('https://api.example.com/users/123'),
      );
      if (response.statusCode == 200) {
        setState(() {
          userData = jsonDecode(response.body);
          isLoading = false;
        });
      } else {
        setState(() {
          errorMessage = 'Failed to load user';
          isLoading = false;
        });
      }
    } catch (e) {
      setState(() {
        errorMessage = e.toString();
        isLoading = false;
      });
    }
  }

  // More API logic in the same file
  Future<void> _saveUser() async {
    setState(() => isLoading = true);
    try {
      await http.put(
        Uri.parse('https://api.example.com/users/123'),
        body: jsonEncode({'name': nameController.text}),
      );
      await _loadUser();  // Reload after save
    } catch (e) {
      setState(() => errorMessage = e.toString());
    }
  }

  @override
  Widget build(BuildContext context) {
    // UI code mixed with all the logic above
    if (isLoading) return CircularProgressIndicator();
    if (errorMessage != null) return Text(errorMessage!);

    return Column(
      children: [
        Text(userData?['name'] ?? ''),
        TextField(controller: nameController),
        ElevatedButton(
          onPressed: _saveUser,
          child: Text('Save'),
        ),
      ],
    );
  }
}

// LAYER 1: MODEL - Just data, nothing else
class User {
  final String id;
  final String name;
  final String email;

  User({required this.id, required this.name, required this.email});

  // Factory to create from JSON
  factory User.fromJson(Map<String, dynamic> json) {
    return User(
      id: json['id'],
      name: json['name'],
      email: json['email'],
    );
  }

  // Convert back to JSON
  Map<String, dynamic> toJson() {
    return {'id': id, 'name': name, 'email': email};
  }
}

// LAYER 2: REPOSITORY - Handles data fetching
class UserRepository {
  final String baseUrl = 'https://api.example.com';

  Future<User> getUser(String id) async {
    final response = await http.get(Uri.parse('$baseUrl/users/$id'));
    if (response.statusCode == 200) {
      return User.fromJson(jsonDecode(response.body));
    }
    throw Exception('Failed to load user');
  }

  Future<void> updateUser(User user) async {
    await http.put(
      Uri.parse('$baseUrl/users/${user.id}'),
      body: jsonEncode(user.toJson()),
    );
  }
}

// LAYER 3: VIEWMODEL - Manages state and logic
// (Using Riverpod - we will learn this soon!)
@riverpod
class UserViewModel extends _$UserViewModel {
  @override
  Future<User> build(String userId) async {
    final repository = ref.read(userRepositoryProvider);
    return repository.getUser(userId);
  }

  Future<void> updateName(String newName) async {
    final user = await future;
    final updatedUser = User(
      id: user.id,
      name: newName,
      email: user.email,
    );
    final repository = ref.read(userRepositoryProvider);
    await repository.updateUser(updatedUser);
    ref.invalidateSelf();  // Refresh data
  }
}

// LAYER 4: VIEW - Only UI, no logic
class UserProfileScreen extends ConsumerWidget {
  final String userId;
  const UserProfileScreen({required this.userId});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final userAsync = ref.watch(userViewModelProvider(userId));

    return userAsync.when(
      loading: () => CircularProgressIndicator(),
      error: (err, _) => Text('Error: $err'),
      data: (user) => Column(
        children: [
          Text(user.name),
          Text(user.email),
          ElevatedButton(
            onPressed: () => ref
                .read(userViewModelProvider(userId).notifier)
                .updateName('New Name'),
            child: Text('Update'),
          ),
        ],
      ),
    );
  }
}

// VISUAL REPRESENTATION OF MVVM
//
//  +------------------+
//  |      VIEW        |  <- Flutter Widgets (UI only)
