import 'dart:convert';
import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:http/http.dart' as http;

// User Model
class User {
  final String id;
  final String name;
  final String email;

  User({required this.id, required this.name, required this.email});

  factory User.fromJson(Map<String, dynamic> json) {
    return User(
      id: json['id'].toString(),
      name: json['name'],
      email: json['email'],
    );
  }
}

// FutureProvider - fetches user data
// Riverpod handles loading/error states automatically!
final userProvider = FutureProvider<User>((ref) async {
  // Simulate network delay for demo
  await Future.delayed(const Duration(seconds: 2));

  // In real app: fetch from API
  final response = await http.get(
    Uri.parse('https://jsonplaceholder.typicode.com/users/1'),
  );

  if (response.statusCode == 200) {
    return User.fromJson(jsonDecode(response.body));
  } else {
    throw Exception('Failed to load user');
  }
});

// Widget using the provider
class UserScreen extends ConsumerWidget {
  const UserScreen({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    // Watch returns AsyncValue<User>
    final userAsync = ref.watch(userProvider);

    return Scaffold(
      appBar: AppBar(title: const Text('User Profile')),
      body: Center(
        // The magic: when() handles all three states!
        child: userAsync.when(
          // LOADING STATE: Show spinner
          loading: () => const Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              CircularProgressIndicator(),
              SizedBox(height: 16),
              Text('Loading user...'),
            ],
          ),

          // ERROR STATE: Show error message
          error: (error, stackTrace) => Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              const Icon(Icons.error, color: Colors.red, size: 48),
              const SizedBox(height: 16),
              Text('Error: $error'),
              const SizedBox(height: 16),
              ElevatedButton(
                // Refresh: invalidate the provider to retry
                onPressed: () => ref.invalidate(userProvider),
                child: const Text('Retry'),
              ),
            ],
          ),

          // DATA STATE: Show the user
          data: (user) => Card(
            margin: const EdgeInsets.all(16),
            child: Padding(
              padding: const EdgeInsets.all(24),
              child: Column(
                mainAxisSize: MainAxisSize.min,
                children: [
                  const CircleAvatar(
                    radius: 40,
                    child: Icon(Icons.person, size: 40),
                  ),
                  const SizedBox(height: 16),
                  Text(
                    user.name,
                    style: const TextStyle(
                      fontSize: 24,
                      fontWeight: FontWeight.bold,
                    ),
                  ),
                  const SizedBox(height: 8),
                  Text(
                    user.email,
                    style: TextStyle(color: Colors.grey[600]),
                  ),
                  const SizedBox(height: 16),
                  ElevatedButton.icon(
                    onPressed: () => ref.invalidate(userProvider),
                    icon: const Icon(Icons.refresh),
                    label: const Text('Refresh'),
                  ),
                ],
              ),
            ),
          ),
        ),
      ),
    );
  }
}

// Main app
void main() {
  runApp(
    const ProviderScope(
      child: MaterialApp(home: UserScreen()),
    ),
  );
}

import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'dart:convert';
import 'package:http/http.dart' as http;

// User model
class User {
  final String id;
  final String name;
  final String email;

  User({required this.id, required this.name, required this.email});

  factory User.fromJson(Map<String, dynamic> json) => User(
    id: json['id'].toString(),
    name: json['name'],
    email: json['email'],
  );

  User copyWith({String? name, String? email}) => User(
    id: id,
    name: name ?? this.name,
    email: email ?? this.email,
  );
}

// AsyncNotifier for complex async state management
class UserNotifier extends AsyncNotifier<User> {
  @override
  Future<User> build() async {
    // This is called when provider is first accessed
    // Returns the initial data (fetched from API)
    return await _fetchUser();
  }

  // Private method to fetch user
  Future<User> _fetchUser() async {
    final response = await http.get(
      Uri.parse('https://jsonplaceholder.typicode.com/users/1'),
    );

    if (response.statusCode == 200) {
      return User.fromJson(jsonDecode(response.body));
    } else {
      throw Exception('Failed to load user');
    }
  }

  // PUBLIC METHOD: Refresh user data
  Future<void> refresh() async {
    // Set state to loading
    state = const AsyncValue.loading();

    // Use AsyncValue.guard to handle errors automatically
    state = await AsyncValue.guard(() => _fetchUser());
  }

  // PUBLIC METHOD: Update user name
  Future<void> updateName(String newName) async {
    // Get current user (if we have data)
    final currentUser = state.valueOrNull;
    if (currentUser == null) return;

    // Set loading state while updating
    state = const AsyncValue.loading();

    // Simulate API call to update
    state = await AsyncValue.guard(() async {
      // In real app: await api.updateUser(currentUser.id, name: newName);
      await Future.delayed(const Duration(seconds: 1));

      // Return updated user
      return currentUser.copyWith(name: newName);
    });
  }

  // PUBLIC METHOD: Update user email  
  Future<void> updateEmail(String newEmail) async {
    final currentUser = state.valueOrNull;
    if (currentUser == null) return;

    state = const AsyncValue.loading();

    state = await AsyncValue.guard(() async {
      await Future.delayed(const Duration(seconds: 1));
      return currentUser.copyWith(email: newEmail);
    });
  }
}

// Create the provider
final userNotifierProvider = AsyncNotifierProvider<UserNotifier, User>(() {
  return UserNotifier();
});

// UNDERSTANDING AsyncValue.guard()
//
// AsyncValue.guard() is a helper that:
// 1. Runs your async function
// 2. If successful: returns AsyncValue.data(result)
// 3. If error: returns AsyncValue.error(error, stackTrace)
//
// Without guard, you would write:
//   try {
//     final result = await someAsyncOperation();
//     state = AsyncValue.data(result);
//   } catch (e, stack) {
//     state = AsyncValue.error(e, stack);
//   }
//
// With guard, just:
//   state = await AsyncValue.guard(() => someAsyncOperation());

// Example usage in widget:
// 
// ElevatedButton(
//   onPressed: () {
//     ref.read(userNotifierProvider.notifier).updateName('New Name');
//   },
//   child: Text('Update Name'),
// )

contentSections.content:
The `when()` method is the primary way to handle AsyncValue. It forces you to handle all three states, making your code robust.

### The when() Method

```dart
final asyncValue = ref.watch(someAsyncProvider);

asyncValue.when(
  loading: () => CircularProgressIndicator(),  // Required
  error: (error, stackTrace) => Text('Error: $error'),  // Required
  data: (value) => Text('Data: $value'),  // Required
);
```

You **must** provide all three callbacks. This ensures you never forget to handle loading or error states.

### Alternative Methods

Riverpod provides other methods for special cases:

**whenOrNull()** - Returns null for unhandled cases:
```dart
// Only handle data, return null for loading/error
final widget = asyncValue.whenOrNull(
  data: (user) => Text(user.name),
);
// widget is null while loading or on error
```

**maybeWhen()** - Provide a default fallback:
```dart
asyncValue.maybeWhen(
  data: (user) => UserCard(user),
  // 'orElse' is called for loading AND error
  orElse: () => CircularProgressIndicator(),
);
```

### Useful Properties

AsyncValue also has helpful properties:

```dart
// Check current state
asyncValue.isLoading   // true if loading
asyncValue.hasValue    // true if has data (even if also loading/error)
asyncValue.hasError    // true if has error

// Get value (nullable)
asyncValue.valueOrNull  // Returns T? (null if no data)
asyncValue.value        // Returns T (throws if no data!)

// Get error (nullable)
asyncValue.error        // Returns Object? (null if no error)
```

### Pattern: Show Previous Data While Loading

A common UX pattern is to show stale data while refreshing:

```dart
Widget build(BuildContext context, WidgetRef ref) {
  final userAsync = ref.watch(userProvider);

  return Stack(
    children: [
      // Show data if available (even during refresh)
      if (userAsync.hasValue)
        UserCard(userAsync.value!),

      // Show loading indicator on top
      if (userAsync.isLoading)
        const Center(child: CircularProgressIndicator()),

      // Show error message
      if (userAsync.hasError)
        ErrorBanner(userAsync.error.toString()),
    ],
  );
}
```

These are the most common mistakes when working with async state in Riverpod.

### Mistake 1: Not Showing Loading State

**The Problem:**
```dart
Widget build(BuildContext context, WidgetRef ref) {
  final userAsync = ref.watch(userProvider);

  // WRONG: Just grabbing value, ignoring loading/error
  final user = userAsync.value;
  return Text(user?.name ?? 'Unknown');
}
```

Users see nothing or stale data while loading. They do not know anything is happening.

**The Fix:**
```dart
return userAsync.when(
  loading: () => CircularProgressIndicator(),
  error: (e, _) => Text('Error: $e'),
  data: (user) => Text(user.name),
);
```

### Mistake 2: Ignoring Errors

**The Problem:**
```dart
// maybeWhen without error handling
return userAsync.maybeWhen(
  data: (user) => UserCard(user),
  orElse: () => CircularProgressIndicator(),
);
// Error shows as loading forever!
```

**The Fix:**
```dart
return userAsync.when(
  loading: () => CircularProgressIndicator(),
  error: (e, _) => ErrorWidget(e),  // Always handle errors!
  data: (user) => UserCard(user),
);
```

### Mistake 3: Calling Async Methods in build() Without Guard

**The Problem:**
```dart
class MyNotifier extends AsyncNotifier<Data> {
  Future<void> refresh() async {
    state = AsyncValue.loading();

    // WRONG: No error handling!
    final data = await fetchData();
    state = AsyncValue.data(data);
    // If fetchData throws, state is stuck on loading!
  }
}
```

**The Fix:**
```dart
Future<void> refresh() async {
  state = const AsyncValue.loading();

  // RIGHT: guard() handles errors automatically
  state = await AsyncValue.guard(() => fetchData());
}
```

### Mistake 4: Not Using AsyncValue.guard()

**The Problem:**
```dart
// Manual try-catch is verbose and error-prone
try {
  final result = await someAsyncOperation();
  state = AsyncValue.data(result);
} catch (e, stack) {
  state = AsyncValue.error(e, stack);
}
```

**The Fix:**
```dart
// guard() does the same thing in one line
state = await AsyncValue.guard(() => someAsyncOperation());
```

### Mistake 5: Forgetting to Set Loading State Before Async Operations

**The Problem:**
```dart
Future<void> updateData() async {
  // Forgot to set loading!
  final newData = await fetchNewData();
  state = AsyncValue.data(newData);
}
// User does not know update is in progress
```

**The Fix:**
```dart
Future<void> updateData() async {
  state = const AsyncValue.loading();  // Tell users something is happening
  state = await AsyncValue.guard(() => fetchNewData());
}
```

