// =====================================================
// RIVERPOD AS DEPENDENCY INJECTION
// =====================================================

import 'package:riverpod_annotation/riverpod_annotation.dart';

part 'providers.g.dart';

// =====================================================
// STEP 1: Define your dependencies as providers
// =====================================================

// Low-level dependency: API client
@riverpod
ApiClient apiClient(Ref ref) {
  return ApiClient(baseUrl: 'https://api.example.com');
}

// Mid-level dependency: Repository (depends on ApiClient)
@riverpod
UserRepository userRepository(Ref ref) {
  // DEPENDENCY INJECTION: Get ApiClient from Riverpod
  final client = ref.watch(apiClientProvider);
  return UserRepository(client);
}

// High-level: ViewModel (depends on Repository)
@riverpod
class UserViewModel extends _$UserViewModel {
  @override
  Future<User> build() async {
    // DEPENDENCY INJECTION: Get Repository from Riverpod
    final repo = ref.watch(userRepositoryProvider);
    return await repo.getCurrentUser();
  }

  Future<void> updateProfile(String name) async {
    final repo = ref.read(userRepositoryProvider);
    state = const AsyncLoading();
    state = await AsyncValue.guard(() => repo.updateUser(name));
  }
}

// =====================================================
// THE DEPENDENCY CHAIN
// =====================================================
//
// UserViewModel
//      |
//      | ref.watch(userRepositoryProvider)
//      v
// UserRepository
//      |
//      | ref.watch(apiClientProvider)
//      v
//   ApiClient
//
// Each level gets its dependencies injected by Riverpod!

// =====================================================
// USAGE IN WIDGET
// =====================================================

class ProfileScreen extends ConsumerWidget {
  @override
  Widget build(BuildContext context, WidgetRef ref) {
    // You don't create dependencies - Riverpod handles it!
    final userAsync = ref.watch(userViewModelProvider);

    return userAsync.when(
      loading: () => CircularProgressIndicator(),
      error: (e, _) => Text('Error: $e'),
      data: (user) => Column(
        children: [
          Text('Hello, ${user.name}'),
          ElevatedButton(
            onPressed: () {
              ref.read(userViewModelProvider.notifier).updateProfile('New Name');
            },
            child: Text('Update Profile'),
          ),
        ],
      ),
    );
  }
}

// Notice: The widget has NO idea how UserViewModel gets its dependencies.
// It just asks Riverpod for the ViewModel, and everything is wired up.

// =====================================================
// COMPLETE DEPENDENCY INJECTION EXAMPLE
// =====================================================

import 'dart:convert';
import 'package:http/http.dart' as http;
import 'package:riverpod_annotation/riverpod_annotation.dart';
import 'package:flutter/material.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';

part 'product_feature.g.dart';

// =====================================================
// LAYER 1: API CLIENT (lowest level)
// =====================================================

class ApiClient {
  final String baseUrl;
  final http.Client httpClient;

  ApiClient({required this.baseUrl, http.Client? client})
      : httpClient = client ?? http.Client();

  Future<Map<String, dynamic>> get(String endpoint) async {
    final response = await httpClient.get(Uri.parse('$baseUrl$endpoint'));
    if (response.statusCode == 200) {
      return jsonDecode(response.body);
    }
    throw Exception('API Error: ${response.statusCode}');
  }

  Future<Map<String, dynamic>> post(String endpoint, Map<String, dynamic> body) async {
    final response = await httpClient.post(
      Uri.parse('$baseUrl$endpoint'),
      headers: {'Content-Type': 'application/json'},
      body: jsonEncode(body),
    );
    if (response.statusCode == 200 || response.statusCode == 201) {
      return jsonDecode(response.body);
    }
    throw Exception('API Error: ${response.statusCode}');
  }
}

// Provider for ApiClient
@Riverpod(keepAlive: true)  // Keep alive - used throughout app
ApiClient apiClient(Ref ref) {
  return ApiClient(baseUrl: 'https://api.example.com');
}

// =====================================================
// LAYER 2: REPOSITORY (data access)
// =====================================================

class Product {
  final String id;
  final String name;
  final double price;
  final String imageUrl;

  Product({
    required this.id,
    required this.name,
    required this.price,
    required this.imageUrl,
  });

  factory Product.fromJson(Map<String, dynamic> json) {
    return Product(
      id: json['id'].toString(),
      name: json['name'],
      price: (json['price'] as num).toDouble(),
      imageUrl: json['image_url'] ?? '',
    );
  }
}

abstract class ProductRepository {
  Future<List<Product>> getProducts();
  Future<Product> getProduct(String id);
  Future<void> addToCart(String productId, int quantity);
}

class ApiProductRepository implements ProductRepository {
  final ApiClient _client;

  ApiProductRepository(this._client);  // Dependency injected!

  @override
  Future<List<Product>> getProducts() async {
    final data = await _client.get('/products');
    final items = data['products'] as List;
    return items.map((item) => Product.fromJson(item)).toList();
  }

  @override
  Future<Product> getProduct(String id) async {
    final data = await _client.get('/products/$id');
    return Product.fromJson(data);
  }

  @override
  Future<void> addToCart(String productId, int quantity) async {
    await _client.post('/cart', {
      'product_id': productId,
      'quantity': quantity,
    });
  }
}

// Provider for ProductRepository - INJECTS ApiClient
@riverpod
ProductRepository productRepository(Ref ref) {
  final apiClient = ref.watch(apiClientProvider);  // DI!
  return ApiProductRepository(apiClient);
}

// =====================================================
// LAYER 3: VIEWMODEL (business logic)
// =====================================================

@riverpod
class ProductList extends _$ProductList {
  @override
  Future<List<Product>> build() async {
    // DEPENDENCY INJECTION: Get repository from Riverpod
    final repository = ref.watch(productRepositoryProvider);
    return await repository.getProducts();
  }

  Future<void> refresh() async {
    state = const AsyncLoading();
    final repository = ref.read(productRepositoryProvider);
    state = await AsyncValue.guard(() => repository.getProducts());
  }

  Future<void> addToCart(String productId) async {
    final repository = ref.read(productRepositoryProvider);
    await repository.addToCart(productId, 1);
    // Could show a snackbar or update cart count here
  }
}

// Single product detail (with parameter)
@riverpod
class ProductDetail extends _$ProductDetail {
  @override
  Future<Product> build(String productId) async {
    final repository = ref.watch(productRepositoryProvider);
    return await repository.getProduct(productId);
  }
}

// =====================================================
// LAYER 4: VIEW (UI)
// =====================================================

class ProductListScreen extends ConsumerWidget {
  const ProductListScreen({super.key});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    // Widget has NO knowledge of repositories or API clients!
    // It just asks for the ViewModel.
    final productsAsync = ref.watch(productListProvider);

    return Scaffold(
      appBar: AppBar(
        title: const Text('Products'),
        actions: [
          IconButton(
            icon: const Icon(Icons.refresh),
            onPressed: () => ref.read(productListProvider.notifier).refresh(),
          ),
        ],
      ),
      body: productsAsync.when(
        loading: () => const Center(child: CircularProgressIndicator()),
        error: (error, _) => Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              Text('Error: $error'),
              ElevatedButton(
                onPressed: () => ref.read(productListProvider.notifier).refresh(),
                child: const Text('Retry'),
              ),
            ],
          ),
        ),
        data: (products) => ListView.builder(
          itemCount: products.length,
          itemBuilder: (context, index) {
            final product = products[index];
            return ListTile(
              title: Text(product.name),
              subtitle: Text('\$${product.price.toStringAsFixed(2)}'),
              trailing: IconButton(
                icon: const Icon(Icons.add_shopping_cart),
                onPressed: () {
                  ref.read(productListProvider.notifier).addToCart(product.id);
                },
              ),
              onTap: () {
                Navigator.push(
                  context,
                  MaterialPageRoute(
                    builder: (_) => ProductDetailScreen(productId: product.id),
                  ),
                );
              },
            );
          },
        ),
      ),
    );
  }
}

class ProductDetailScreen extends ConsumerWidget {
  final String productId;

  const ProductDetailScreen({super.key, required this.productId});

  @override
  Widget build(BuildContext context, WidgetRef ref) {
    final productAsync = ref.watch(productDetailProvider(productId));

    return Scaffold(
      appBar: AppBar(title: const Text('Product Detail')),
      body: productAsync.when(
        loading: () => const Center(child: CircularProgressIndicator()),
        error: (e, _) => Center(child: Text('Error: $e')),
        data: (product) => Padding(
          padding: const EdgeInsets.all(16),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Text(product.name, style: Theme.of(context).textTheme.headlineMedium),
              const SizedBox(height: 8),
              Text('\$${product.price.toStringAsFixed(2)}',
                  style: Theme.of(context).textTheme.titleLarge),
              const SizedBox(height: 16),
              ElevatedButton(
                onPressed: () {
                  ref.read(productListProvider.notifier).addToCart(product.id);
                  ScaffoldMessenger.of(context).showSnackBar(
                    const SnackBar(content: Text('Added to cart!')),
                  );
                },
                child: const Text('Add to Cart'),
              ),
            ],
          ),
        ),
      ),
    );
  }
}

// =====================================================
// THE DEPENDENCY CHAIN IS CLEAR AND TESTABLE
// =====================================================
//
// ProductListScreen / ProductDetailScreen
//           |
//           | ref.watch(productListProvider / productDetailProvider)
//           v
// ProductList / ProductDetail (ViewModels)
//           |
//           | ref.watch(productRepositoryProvider)
//           v
//    ProductRepository
//           |
//           | ref.watch(apiClientProvider)
//           v
//       ApiClient
//
// Each layer can be tested independently by overriding providers!

// =====================================================
// TESTING WITH PROVIDER OVERRIDES
// =====================================================

import 'package:flutter_test/flutter_test.dart';
import 'package:flutter_riverpod/flutter_riverpod.dart';
import 'package:flutter/material.dart';

// Mock repository for testing
class MockProductRepository implements ProductRepository {
  final List<Product> _products;
  final bool shouldFail;

  MockProductRepository({
    List<Product>? products,
    this.shouldFail = false,
  }) : _products = products ?? [
         Product(id: '1', name: 'Test Product 1', price: 9.99, imageUrl: ''),
         Product(id: '2', name: 'Test Product 2', price: 19.99, imageUrl: ''),
       ];

  @override
  Future<List<Product>> getProducts() async {
    // Simulate network delay
    await Future.delayed(Duration(milliseconds: 10));

    if (shouldFail) {
      throw Exception('Network error');
    }

    return _products;
  }

  @override
  Future<Product> getProduct(String id) async {
    await Future.delayed(Duration(milliseconds: 10));

    if (shouldFail) {
      throw Exception('Network error');
    }

    return _products.firstWhere((p) => p.id == id);
  }

  @override
  Future<void> addToCart(String productId, int quantity) async {
    await Future.delayed(Duration(milliseconds: 10));
    if (shouldFail) {
      throw Exception('Failed to add to cart');
    }
  }
}

// =====================================================
// WIDGET TEST: Override repository
// =====================================================

void main() {
  group('ProductListScreen', () {
    testWidgets('displays list of products', (tester) async {
      await tester.pumpWidget(
        ProviderScope(
          overrides: [
            // Override with mock that returns specific products
            productRepositoryProvider.overrideWith((ref) => MockProductRepository(
              products: [
                Product(id: '1', name: 'Widget', price: 5.00, imageUrl: ''),
                Product(id: '2', name: 'Gadget', price: 10.00, imageUrl: ''),
              ],
            )),
          ],
          child: MaterialApp(home: ProductListScreen()),
        ),
      );

      // Initially shows loading
      expect(find.byType(CircularProgressIndicator), findsOneWidget);

      // Wait for async data to load
      await tester.pumpAndSettle();

      // Now shows products
      expect(find.text('Widget'), findsOneWidget);
      expect(find.text('Gadget'), findsOneWidget);
      expect(find.text('\$5.00'), findsOneWidget);
      expect(find.text('\$10.00'), findsOneWidget);
    });

    testWidgets('shows error state when fetch fails', (tester) async {
      await tester.pumpWidget(
        ProviderScope(
          overrides: [
            // Override with mock that always fails
            productRepositoryProvider.overrideWith((ref) => MockProductRepository(
              shouldFail: true,
            )),
          ],
          child: MaterialApp(home: ProductListScreen()),
        ),
      );

      // Wait for async operation
      await tester.pumpAndSettle();

      // Shows error message and retry button
      expect(find.textContaining('Error'), findsOneWidget);
      expect(find.text('Retry'), findsOneWidget);
    });

    testWidgets('retry button refreshes data', (tester) async {
      final mockRepo = MockProductRepository(shouldFail: true);

      await tester.pumpWidget(
        ProviderScope(
          overrides: [
            productRepositoryProvider.overrideWith((ref) => mockRepo),
          ],
          child: MaterialApp(home: ProductListScreen()),
        ),
      );

      await tester.pumpAndSettle();

      // Tap retry button
      await tester.tap(find.text('Retry'));
      await tester.pump();

      // Should show loading indicator during refresh
      expect(find.byType(CircularProgressIndicator), findsOneWidget);
    });
  });
}

// =====================================================
// UNIT TEST: Test ViewModel in isolation
// =====================================================

void main() {
  group('ProductList ViewModel', () {
    test('loads products on initialization', () async {
      // Create a container with mock repository
      final container = ProviderContainer(
        overrides: [
          productRepositoryProvider.overrideWith((ref) => MockProductRepository()),
        ],
      );

      // Read the provider
      final notifier = container.read(productListProvider.notifier);

      // Wait for async initialization
      await container.read(productListProvider.future);

      // Check state
      final state = container.read(productListProvider);
      expect(state.hasValue, isTrue);
      expect(state.value!.length, equals(2));

      // Clean up
      container.dispose();
    });

    test('refresh updates products', () async {
      final container = ProviderContainer(
        overrides: [
          productRepositoryProvider.overrideWith((ref) => MockProductRepository()),
        ],
      );

      // Wait for initial load
      await container.read(productListProvider.future);

      // Refresh
      await container.read(productListProvider.notifier).refresh();

      // Verify state after refresh
      final state = container.read(productListProvider);
      expect(state.hasValue, isTrue);

      container.dispose();
    });
  });
}

// =====================================================
// ENVIRONMENT-SPECIFIC CONFIGURATION
// =====================================================

enum Environment { development, staging, production }

ProviderScope buildApp(Environment env) {
  final overrides = <Override>[];

  switch (env) {
    case Environment.development:
      overrides.add(
        apiClientProvider.overrideWith((ref) => ApiClient(
          baseUrl: 'http://localhost:8080',
        )),
      );
      break;
    case Environment.staging:
      overrides.add(
        apiClientProvider.overrideWith((ref) => ApiClient(
          baseUrl: 'https://staging.example.com',
        )),
      );
      break;
    case Environment.production:
      // Use default production URL from provider
      break;
  }

  return ProviderScope(
    overrides: overrides,
    child: MyApp(),
  );
}

// main_development.dart
void main() {
  runApp(buildApp(Environment.development));
}

// main_staging.dart
void main() {
  runApp(buildApp(Environment.staging));
}

// main_production.dart
void main() {
  runApp(buildApp(Environment.production));
}

