// THE MESSY VERSION - Everything in one StatefulWidget

import 'package:flutter/material.dart';

void main() => runApp(const MaterialApp(home: MessyNotesApp()));

class MessyNotesApp extends StatefulWidget {
  const MessyNotesApp({super.key});

  @override
  State<MessyNotesApp> createState() => _MessyNotesAppState();
}

class _MessyNotesAppState extends State<MessyNotesApp> {
  // Problem: State stored directly in widget
  final List<Map<String, String>> _notes = [];

  // Problem: Business logic mixed with UI
  void _addNote(String title, String content) {
    setState(() {
      _notes.add({
        'id': DateTime.now().millisecondsSinceEpoch.toString(),
        'title': title,
        'content': content,
        'createdAt': DateTime.now().toIso8601String(),
      });
    });
  }

  void _updateNote(String id, String title, String content) {
    setState(() {
      final index = _notes.indexWhere((n) => n['id'] == id);
      if (index != -1) {
        _notes[index] = {
          ..._notes[index],
          'title': title,
          'content': content,
        };
      }
    });
  }

  void _deleteNote(String id) {
    setState(() {
      _notes.removeWhere((n) => n['id'] == id);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('My Notes')),
      body: _notes.isEmpty
          ? const Center(child: Text('No notes yet'))
          : ListView.builder(
              itemCount: _notes.length,
              itemBuilder: (context, index) {
                final note = _notes[index];
                return ListTile(
                  title: Text(note['title'] ?? ''),
                  subtitle: Text(note['content'] ?? ''),
                  trailing: IconButton(
                    icon: const Icon(Icons.delete),
                    onPressed: () => _deleteNote(note['id']!),
                  ),
                );
              },
            ),
      floatingActionButton: FloatingActionButton(
        onPressed: () => _showAddDialog(context),
        child: const Icon(Icons.add),
      ),
    );
  }

  void _showAddDialog(BuildContext context) {
    final titleController = TextEditingController();
    final contentController = TextEditingController();
    showDialog(
      context: context,
      builder: (ctx) => AlertDialog(
        title: const Text('New Note'),
        content: Column(
          mainAxisSize: MainAxisSize.min,
          children: [
            TextField(controller: titleController, decoration: const InputDecoration(labelText: 'Title')),
            TextField(controller: contentController, decoration: const InputDecoration(labelText: 'Content')),
          ],
        ),
        actions: [
          TextButton(onPressed: () => Navigator.pop(ctx), child: const Text('Cancel')),
          TextButton(
            onPressed: () {
              _addNote(titleController.text, contentController.text);
              Navigator.pop(ctx);
            },
            child: const Text('Save'),
          ),
        ],
      ),
    );
  }
}

// models/note.dart

class Note {
  final String id;
  final String title;
  final String content;
  final DateTime createdAt;

  const Note({
    required this.id,
    required this.title,
    required this.content,
    required this.createdAt,
  });

  // Factory constructor for creating new notes
  factory Note.create({required String title, required String content}) {
    return Note(
      id: DateTime.now().millisecondsSinceEpoch.toString(),
      title: title,
      content: content,
      createdAt: DateTime.now(),
    );
  }

  // copyWith for immutable updates
  Note copyWith({
    String? id,
    String? title,
    String? content,
    DateTime? createdAt,
  }) {
    return Note(
      id: id ?? this.id,
      title: title ?? this.title,
      content: content ?? this.content,
      createdAt: createdAt ?? this.createdAt,
    );
  }

  @override
  String toString() => 'Note(id: $id, title: $title)';

  @override
  bool operator ==(Object other) =>
      identical(this, other) ||
      other is Note &&
          runtimeType == other.runtimeType &&
          id == other.id &&
          title == other.title &&
          content == other.content &&
          createdAt == other.createdAt;

  @override
  int get hashCode => Object.hash(id, title, content, createdAt);
}

// viewmodels/notes_viewmodel.dart

import 'package:riverpod_annotation/riverpod_annotation.dart';
import '../models/note.dart';

part 'notes_viewmodel.g.dart';

@riverpod
class NotesViewModel extends _$NotesViewModel {
  @override
  List<Note> build() {
    // Initial state: empty list
    // In a real app, you might load from a database here
    return [];
  }

  /// Add a new note
  void addNote({required String title, required String content}) {
    // Validate input
    if (title.trim().isEmpty) return;

    // Create new note using factory
    final note = Note.create(title: title.trim(), content: content.trim());

    // Update state immutably
    state = [...state, note];
  }

  /// Update an existing note
  void updateNote({
    required String id,
    required String title,
    required String content,
  }) {
    state = state.map((note) {
      if (note.id == id) {
        return note.copyWith(
          title: title.trim(),
          content: content.trim(),
        );
      }
      return note;
    }).toList();
  }

  /// Delete a note by ID
  void deleteNote(String id) {
    state = state.where((note) => note.id != id).toList();
  }

  /// Get a single note by ID (useful for detail screens)
  Note? getNoteById(String id) {
    try {
      return state.firstWhere((note) => note.id == id);
    } catch (e) {
      return null;
    }
  }
}

