Widget _buildSettingsList(List<String> items) {
  return ListView.builder(
    itemCount: items.length,
    itemBuilder: (context, index) {
      return SwitchListTile(
        title: Text(items[index]),
        value: index % 2 == 0,
        onChanged: (value) {},
      );
    },
  );
}

