// Messaging App Challenge
// Chat with typing indicators and read receipts

import 'package:flutter/material.dart';

void main() {
  runApp(const MessagingApp());
}

class MessagingApp extends StatelessWidget {
  const MessagingApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      theme: ThemeData(useMaterial3: true),
      home: const ChatScreen(),
    );
  }
}

// Message model
class Message {
  final String id;
  final String text;
  final bool isMe;
  final DateTime timestamp;
  final bool isRead;

  Message({
    required this.id,
    required this.text,
    required this.isMe,
    required this.timestamp,
    this.isRead = false,
  });
}

class ChatScreen extends StatefulWidget {
  const ChatScreen({super.key});

  @override
  State<ChatScreen> createState() => _ChatScreenState();
}

class _ChatScreenState extends State<ChatScreen> {
  final TextEditingController _controller = TextEditingController();
  final List<Message> _messages = [];
  bool _isOtherTyping = false;

  @override
  void dispose() {
    _controller.dispose();
    super.dispose();
  }

  void _sendMessage() {
    if (_controller.text.trim().isEmpty) return;

    setState(() {
      _messages.add(Message(
        id: DateTime.now().toString(),
        text: _controller.text,
        isMe: true,
        timestamp: DateTime.now(),
      ));
    });
    _controller.clear();

    // TODO 1: Simulate other user typing after a delay
    _simulateOtherUserResponse();
  }

  void _simulateOtherUserResponse() {
    // Show typing indicator
    setState(() => _isOtherTyping = true);

    // Simulate typing delay
    Future.delayed(const Duration(seconds: 2), () {
      if (mounted) {
        setState(() {
          _isOtherTyping = false;
          _messages.add(Message(
            id: DateTime.now().toString(),
            text: 'Thanks for your message!',
            isMe: false,
            timestamp: DateTime.now(),
          ));
          // Mark previous messages as read
          // TODO 2: Update isRead for sent messages
        });
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Chat'),
