// routes/_middleware.dart
import 'package:dart_frog/dart_frog.dart';

Handler middleware(Handler handler) {
  return (context) async {
    // ============================================
    // BEFORE the route handler runs
    // ============================================
    print('Request: ${context.request.method} ${context.request.uri}');
    final stopwatch = Stopwatch()..start();

    // ============================================
    // Call the next handler (your route)
    // ============================================
    final response = await handler(context);

    // ============================================
    // AFTER the route handler completes
    // ============================================
    stopwatch.stop();
    print('Response: ${response.statusCode} in ${stopwatch.elapsedMilliseconds}ms');

    return response;
  };
}

// routes/_middleware.dart
import 'package:dart_frog/dart_frog.dart';

Handler middleware(Handler handler) {
  return (context) async {
    // Handle preflight OPTIONS requests
    if (context.request.method == HttpMethod.options) {
      return Response(
        statusCode: 200,
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS',
          'Access-Control-Allow-Headers': 'Origin, Content-Type, Authorization',
        },
      );
    }

    // Call the route handler
    final response = await handler(context);

    // Add CORS headers to all responses
    return response.copyWith(
      headers: {
        ...response.headers,
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS',
        'Access-Control-Allow-Headers': 'Origin, Content-Type, Authorization',
      },
    );
  };
}

// Now any route in this folder (and subfolders) will have CORS headers!
// Example: GET /api/users will include Access-Control-Allow-Origin: *

