import 'package:serverpod/serverpod.dart';
import '../generated/protocol.dart';

// Store connected sessions by channel
// In production, consider using Serverpod's built-in session management
final Map<String, Set<StreamingSession>> _channelSubscribers = {};

class ChatEndpoint extends Endpoint {
  /// Handle streaming connections.
  /// This method is called when a client opens a streaming connection.
  @override
  Future<void> streamOpened(StreamingSession session) async {
    // Log connection for debugging
    session.log('Client connected to chat streaming');

    // You can access user info if authenticated
    // final userId = await session.auth.authenticatedUserId;
  }

  /// Handle messages from clients.
  /// This is called whenever a client sends a message through the stream.
  @override
  Future<void> handleStreamMessage(
    StreamingSession session,
    SerializableModel message,
  ) async {
    // Determine message type and handle accordingly
    if (message is ChatMessage) {
      await _handleChatMessage(session, message);
    } else if (message is ChannelSubscription) {
      await _handleSubscription(session, message);
    }
  }

  /// Clean up when client disconnects.
  @override
  Future<void> streamClosed(StreamingSession session) async {
    session.log('Client disconnected from chat streaming');

    // Remove from all channels
    for (final subscribers in _channelSubscribers.values) {
      subscribers.remove(session);
    }
  }

  // Handle incoming chat messages
  Future<void> _handleChatMessage(
    StreamingSession session,
    ChatMessage message,
  ) async {
    // Add timestamp if not set
    final messageWithTimestamp = ChatMessage(
      senderName: message.senderName,
      content: message.content,
      timestamp: DateTime.now(),
      channelId: message.channelId,
    );

    // Broadcast to all subscribers of this channel
    final subscribers = _channelSubscribers[message.channelId] ?? {};
    for (final subscriber in subscribers) {
      // Send the message to each connected client
      subscriber.sendStreamMessage(messageWithTimestamp);
    }

    // Optionally persist to database
    // await ChatMessage.db.insertRow(session, messageWithTimestamp);
  }

  // Handle channel subscription requests
  Future<void> _handleSubscription(
    StreamingSession session,
    ChannelSubscription subscription,
  ) async {
    final channelId = subscription.channelId;

    // Create channel set if it does not exist
    _channelSubscribers[channelId] ??= {};

    if (subscription.subscribe) {
      _channelSubscribers[channelId]!.add(session);
      session.log('Subscribed to channel: $channelId');
    } else {
      _channelSubscribers[channelId]!.remove(session);
      session.log('Unsubscribed from channel: $channelId');
    }
  }
}

// File: lib/services/chat_service.dart

import 'dart:async';
import 'package:my_app_client/my_app_client.dart';

class ChatService {
  final Client _client;
  StreamSubscription? _messageSubscription;
  bool _isConnected = false;

  // Stream controller for UI to listen to messages
  final _messageController = StreamController<ChatMessage>.broadcast();
  Stream<ChatMessage> get messages => _messageController.stream;

  // Connection status stream
  final _connectionController = StreamController<bool>.broadcast();
  Stream<bool> get connectionStatus => _connectionController.stream;

  ChatService(this._client);

  /// Connect to the chat streaming endpoint.
  Future<void> connect() async {
    if (_isConnected) return; // Already connected

    try {
      // Open streaming connection to the chat endpoint
      // This calls streamOpened on the server
      await _client.openStreamingConnection(
        // Optional: reconnection settings
        disconnectOnLostInternetConnection: false,
      );

      // Listen for incoming messages
      _messageSubscription = _client.chat.stream.listen(
        (message) {
          // Route messages by type
          if (message is ChatMessage) {
            _messageController.add(message);
          }
        },
        onError: (error) {
          print('Stream error: $error');
          _handleDisconnection();
        },
        onDone: () {
          print('Stream closed');
          _handleDisconnection();
        },
      );

      _isConnected = true;
      _connectionController.add(true);
      print('Connected to chat stream');

    } catch (e) {
      print('Failed to connect: $e');
      _connectionController.add(false);
      rethrow;
    }
  }

  /// Subscribe to a specific chat channel.
  Future<void> joinChannel(String channelId) async {
    if (!_isConnected) {
      throw StateError('Not connected. Call connect() first.');
    }

    // Send subscription request to server
    _client.chat.sendStreamMessage(
      ChannelSubscription(
        channelId: channelId,
        subscribe: true,
      ),
    );
  }

  /// Leave a chat channel.
  Future<void> leaveChannel(String channelId) async {
    if (!_isConnected) return;

    _client.chat.sendStreamMessage(
      ChannelSubscription(
        channelId: channelId,
        subscribe: false,
      ),
    );
  }

  /// Send a message to a channel.
  Future<void> sendMessage({
    required String channelId,
    required String senderName,
    required String content,
  }) async {
    if (!_isConnected) {
      throw StateError('Not connected. Call connect() first.');
    }

    _client.chat.sendStreamMessage(
      ChatMessage(
        channelId: channelId,
        senderName: senderName,
        content: content,
        timestamp: DateTime.now(), // Server will override
      ),
    );
  }

  void _handleDisconnection() {
    _isConnected = false;
    _connectionController.add(false);
  }

  /// Disconnect from streaming.
  Future<void> disconnect() async {
    await _messageSubscription?.cancel();
    await _client.closeStreamingConnection();
    _isConnected = false;
    _connectionController.add(false);
  }

  /// Clean up resources.
  void dispose() {
    disconnect();
    _messageController.close();
    _connectionController.close();
  }
}

contentSections.content:
Network connections are unreliable. Users move between WiFi and cellular, go through tunnels, or have momentary dropouts. A robust real-time app must handle disconnections gracefully.

**Serverpod's Built-in Reconnection:**

Serverpod's client includes automatic reconnection logic. When you open a streaming connection, you can configure reconnection behavior:

```dart
await client.openStreamingConnection(
  disconnectOnLostInternetConnection: false, // Keep trying to reconnect
);
```

**Manual Reconnection Strategy:**

For more control, implement your own reconnection logic:

```dart
class ReconnectingChatService {
  final Client _client;
  Timer? _reconnectTimer;
  int _reconnectAttempts = 0;
  static const _maxReconnectAttempts = 5;
  static const _reconnectDelays = [1, 2, 4, 8, 16]; // Seconds

  Future<void> _handleDisconnection() async {
    if (_reconnectAttempts >= _maxReconnectAttempts) {
      // Give up and notify user
      _notifyConnectionFailed();
      return;
    }

    // Exponential backoff
    final delay = _reconnectDelays[_reconnectAttempts];
    _reconnectAttempts++;

    _reconnectTimer = Timer(Duration(seconds: delay), () async {
      try {
        await connect();
        _reconnectAttempts = 0; // Reset on success
      } catch (e) {
        _handleDisconnection(); // Try again
      }
    });
  }
}
```

**Best Practices for Reconnection:**

1. **Exponential Backoff**: Wait longer between each attempt (1s, 2s, 4s, 8s...). This prevents overwhelming the server when it comes back online.

2. **Maximum Attempts**: Set a limit. After 5-10 failures, stop trying and show the user an error.

3. **Visual Feedback**: Show connection status in your UI. Users should know if they are offline.

4. **Queue Messages**: Store unsent messages locally. Send them when reconnected.

5. **Request Missed Messages**: After reconnecting, ask the server for messages sent while you were offline.

**Mistake 1: Not Cleaning Up on Disconnect**

If you add sessions to collections but never remove them, you leak memory and send messages to dead connections.

```dart
// BAD: Never removes sessions
@override
Future<void> streamOpened(StreamingSession session) async {
  _allSessions.add(session); // Added here...
}
// streamClosed never implemented - memory leak!

// GOOD: Always clean up
@override
Future<void> streamClosed(StreamingSession session) async {
  _allSessions.remove(session); // ...removed here
}
```

**Mistake 2: Sending Non-Serializable Objects**

Only SerializableModel instances (from your protocol) can be sent through streams. Sending raw Maps or custom classes fails.

```dart
// BAD: Will fail at runtime
session.sendStreamMessage({'type': 'hello'}); // Not a SerializableModel

// GOOD: Use protocol models
session.sendStreamMessage(ChatMessage(content: 'hello', ...));
```

**Mistake 3: Blocking in Message Handlers**

Long-running operations in handleStreamMessage block the entire connection. Use async properly or offload work.

```dart
// BAD: Blocks the stream
@override
Future<void> handleStreamMessage(...) async {
  await expensiveDatabaseOperation(); // Blocks for 5 seconds
  await anotherSlowOperation(); // User is waiting
}

// GOOD: Respond quickly, process in background
@override
Future<void> handleStreamMessage(...) async {
  // Acknowledge immediately
  session.sendStreamMessage(MessageReceived(id: message.id));

  // Process in background (fire and forget)
  unawaited(_processMessageAsync(message));
}
```

**Mistake 4: No Connection State Tracking**

Sending messages before connected or after disconnected causes errors.

```dart
// BAD: No state check
void sendMessage(String text) {
  _client.chat.sendStreamMessage(message); // Might crash
}

// GOOD: Check state first
void sendMessage(String text) {
  if (!_isConnected) {
    throw StateError('Cannot send: not connected');
  }
  _client.chat.sendStreamMessage(message);
}
```

