// class: EmailQueueItem
// table: email_queue
// fields:
//   to: String
//   subject: String
//   bodyHtml: String
//   bodyText: String?
//   status: String          # pending, sending, sent, failed
//   attempts: int
//   maxAttempts: int
//   lastError: String?
