import 'dart:typed_data';
import 'package:serverpod/serverpod.dart';
import '../generated/protocol.dart';

class FileEndpoint extends Endpoint {
  // Maximum file size: 25 MB
  static const int maxFileSize = 25 * 1024 * 1024;

  // Allowed MIME types for chat attachments
  static const allowedMimeTypes = [
    // Images
    'image/jpeg',
    'image/png',
    'image/gif',
    'image/webp',
    // Documents
    'application/pdf',
    'application/msword',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    // Archives
    'application/zip',
    // Audio
    'audio/mpeg',
    'audio/wav',
    // Video
    'video/mp4',
    'video/webm',
  ];

  /// Get a signed upload URL for direct-to-cloud upload.
  /// Client uploads directly to cloud storage, then calls confirmUpload.
  Future<String> getUploadUrl(
    Session session, {
    required String fileName,
    required String mimeType,
    required int fileSize,
    required int chatRoomId,
  }) async {
    await _requireAuth(session);
    await _requireMembership(session, chatRoomId);

    // Validate file
    if (fileSize > maxFileSize) {
      throw InvalidParameterException(
        'File too large. Maximum size is ${maxFileSize ~/ (1024 * 1024)} MB',
      );
    }

    if (!allowedMimeTypes.contains(mimeType)) {
      throw InvalidParameterException(
        'File type not allowed: $mimeType',
      );
    }

    // Generate unique path
    final timestamp = DateTime.now().millisecondsSinceEpoch;
    final sanitizedName = _sanitizeFileName(fileName);
    final path = 'chat/$chatRoomId/$timestamp-$sanitizedName';

    // Get signed upload URL from Serverpod cloud storage
    // This allows the client to upload directly to S3/GCS
    final uploadUrl = await session.storage.createDirectFileUploadDescription(
      storageId: 'public', // Configure in config/production.yaml
      path: path,
    );

    return uploadUrl.uploadUrl ?? '';
  }

  /// Alternative: Upload file data directly through the server.
  /// Use this for smaller files or when direct upload is not available.
  Future<String> uploadFile(
    Session session, {
    required String fileName,
    required String mimeType,
    required Uint8List fileData,
    required int chatRoomId,
  }) async {
    await _requireAuth(session);
    await _requireMembership(session, chatRoomId);

    // Validate
    if (fileData.length > maxFileSize) {
      throw InvalidParameterException(
        'File too large. Maximum size is ${maxFileSize ~/ (1024 * 1024)} MB',
      );
    }

    if (!allowedMimeTypes.contains(mimeType)) {
      throw InvalidParameterException('File type not allowed: $mimeType');
    }

    // Generate unique path
    final timestamp = DateTime.now().millisecondsSinceEpoch;
    final sanitizedName = _sanitizeFileName(fileName);
    final path = 'chat/$chatRoomId/$timestamp-$sanitizedName';

    // Upload via Serverpod storage
    final stream = Stream.fromIterable([fileData]);
    await session.storage.storeFile(
      storageId: 'public',
      path: path,
      stream: stream,
      length: fileData.length,
    );

    // Get public URL
    final url = await session.storage.getPublicUrl(
      storageId: 'public',
      path: path,
    );

    return url ?? '';
  }

  /// Delete a file (only by message sender or room admin).
  Future<bool> deleteFile(
    Session session, {
    required int messageId,
  }) async {
    final currentUser = await _getCurrentUser(session);

    final message = await ChatMessage.db.findById(session, messageId);
    if (message == null) {
      throw NotFoundException('Message not found');
    }

    // Check permission
    final membership = await _requireMembership(session, message.chatRoomId);
    if (message.senderId != currentUser.id && membership.role != 'admin') {
      throw ForbiddenException('Cannot delete this file');
    }

    // Delete from storage
    if (message.attachmentUrl != null) {
      // Extract path from URL and delete
      // Implementation depends on your storage setup
    }

    return true;
  }

  /// Sanitize file name for storage.
  String _sanitizeFileName(String fileName) {
    // Remove path separators and special characters
    return fileName
        .replaceAll(RegExp(r'[/\\:*?"<>|]'), '_')
        .replaceAll(RegExp(r'\s+'), '_')
        .toLowerCase();
  }

  // Helper methods (same as other endpoints)
  Future<int> _requireAuth(Session session) async {
    final authUserId = await session.auth.authenticatedUserId;
    if (authUserId == null) {
      throw AuthenticationRequiredException();
    }
    return authUserId;
  }

  Future<ChatUser> _getCurrentUser(Session session) async {
    final authUserId = await session.auth.authenticatedUserId;
    if (authUserId == null) {
      throw AuthenticationRequiredException();
    }

    final user = await ChatUser.db.findFirstRow(
      session,
      where: (t) => t.userInfoId.equals(authUserId),
    );

    if (user == null) {
      throw NotFoundException('User profile not found');
    }

    return user;
  }

  Future<ChatMember> _requireMembership(
    Session session,
    int roomId,
  ) async {
    final currentUser = await _getCurrentUser(session);

    final membership = await ChatMember.db.findFirstRow(
      session,
      where: (t) => 
        t.chatRoomId.equals(roomId) & 
        t.userId.equals(currentUser.id!),
    );

    if (membership == null) {
      throw ForbiddenException('Not a member of this room');
    }

    return membership;
  }
}

contentSections.content:
Before deploying to production, consider these enhancements:

**1. Multi-Server Support:**
The current implementation uses in-memory state for connected sessions. For multiple server instances, use Redis pub/sub:

```dart
// Store sessions in Redis
await redis.sAdd('room:$roomId:sessions', sessionId);

// Broadcast via Redis pub/sub
await redis.publish('chat:$roomId', messageJson);
```

**2. Rate Limiting:**
Protect against spam and abuse:

```dart
// In handleStreamMessage
final rateKey = 'rate:${user.userId}';
final count = await redis.incr(rateKey);
await redis.expire(rateKey, 60); // Per minute

if (count > 30) { // Max 30 messages per minute
  throw RateLimitExceededException();
}
```

**3. Message Validation:**
Sanitize content to prevent XSS and injection:

```dart
String sanitizeContent(String content) {
  // Remove HTML tags
  content = content.replaceAll(RegExp(r'<[^>]*>'), '');
  // Escape special characters
  content = HtmlEscape().convert(content);
  return content;
}
```

**4. Database Optimization:**
Add indexes for common queries:

```sql
-- Fast room lookup for user
CREATE INDEX idx_chat_member_user_room 
ON chat_members(user_id, chat_room_id);

-- Fast message history
CREATE INDEX idx_chat_message_room_time 
ON chat_messages(chat_room_id, created_at DESC);
```

**5. Monitoring:**
Add logging and metrics:

```dart
session.log('User ${user.username} sent message to room $roomId');
metrics.increment('messages.sent', tags: {'room_type': room.isGroup ? 'group' : 'dm'});
```

**6. Push Notifications:**
Notify offline users via FCM/APNs when they receive messages.

Testing a real-time chat backend requires multiple approaches:

**1. Unit Tests:**
Test individual methods in isolation:

```dart
test('sanitizeFileName removes special characters', () {
  expect(
    fileEndpoint.sanitizeFileName('My File (1).pdf'),
    equals('my_file__1_.pdf'),
  );
});
```

**2. Integration Tests:**
Test endpoint behavior with database:

```dart
withServerpod('Chat message flow', (sessionBuilder) {
  test('can send and receive messages', () async {
    final session = sessionBuilder.build();

    // Create users and room
    final user1 = await createTestUser(session, 'user1');
    final user2 = await createTestUser(session, 'user2');
    final room = await chatRoomEndpoint.getOrCreateDirectMessage(
      session,
      user2.id!,
    );

    // Send message
    final message = await chatMessageEndpoint.sendMessage(
      session,
      chatRoomId: room.id!,
      content: 'Hello!',
    );

    expect(message.content, equals('Hello!'));
    expect(message.senderId, equals(user1.id));
  });
});
```

**3. WebSocket Tests:**
Test streaming with multiple clients:

```dart
test('messages broadcast to all subscribers', () async {
  // Connect two clients
  final client1 = await connectStreamingClient(user1Token);
  final client2 = await connectStreamingClient(user2Token);

  // Subscribe to same room
  await client1.subscribeToRoom(roomId);
  await client2.subscribeToRoom(roomId);

  // Send message from client1
  await client1.sendMessage('Hello from client 1');

  // Verify client2 receives it
  final received = await client2.messages.first;
  expect(received.content, equals('Hello from client 1'));
});
```

**4. Load Testing:**
Use tools like k6 or Artillery:

```javascript
// k6 script
import ws from 'k6/ws';

export default function() {
  const url = 'wss://your-server/chat';
  const res = ws.connect(url, {}, function(socket) {
    socket.on('open', () => {
      socket.send(JSON.stringify({type: 'subscribe', roomId: 1}));
    });
    socket.on('message', (data) => {
      // Handle messages
    });
  });
}
```

**5. Manual Testing:**
Run the server and use multiple Flutter apps or a WebSocket client like Postman to test real-time behavior.

