Let us start with the most basic test - verifying a simple route returns the expected response.

Consider this route handler in routes/health.dart:

```dart
import 'package:dart_frog/dart_frog.dart';

Response onRequest(RequestContext context) {
  return Response.json({
    'status': 'healthy',
    'timestamp': DateTime.now().toIso8601String(),
  });
}
```

Here is how to test it:

contentSections.code:
// test/routes/health_test.dart
import 'dart:convert';
import 'package:dart_frog/dart_frog.dart';
import 'package:mocktail/mocktail.dart';
import 'package:test/test.dart';

import '../../routes/health.dart' as route;

class MockRequestContext extends Mock implements RequestContext {}

void main() {
  group('GET /health', () {
    late MockRequestContext context;

    setUp(() {
      context = MockRequestContext();
    });

    test('returns 200 with healthy status', () async {
      final response = route.onRequest(context);

      expect(response.statusCode, equals(200));

      final body = await response.body();
      final json = jsonDecode(body) as Map<String, dynamic>;

      expect(json['status'], equals('healthy'));
      expect(json['timestamp'], isNotNull);
    });

    test('returns JSON content type', () async {
      final response = route.onRequest(context);

      expect(
        response.headers['content-type'],
        contains('application/json'),
      );
    });
  });
}

// test/helpers/mock_context.dart
import 'dart:convert';
import 'package:dart_frog/dart_frog.dart';
import 'package:mocktail/mocktail.dart';

class MockRequestContext extends Mock implements RequestContext {}
class MockRequest extends Mock implements Request {}
class MockUri extends Mock implements Uri {}

/// Creates a fully configured mock context for testing
MockRequestContext createMockContext({
  String method = 'GET',
  Map<String, String>? headers,
  Map<String, String>? pathParameters,
  Map<String, String>? queryParameters,
  Object? body,
  String path = '/',
}) {
  final context = MockRequestContext();
  final request = MockRequest();
  final uri = MockUri();

  // Configure URI
  when(() => uri.path).thenReturn(path);
  when(() => uri.queryParameters).thenReturn(queryParameters ?? {});

  // Configure Request
  when(() => request.method).thenReturn(HttpMethod(method));
  when(() => request.headers).thenReturn(headers ?? {});
  when(() => request.uri).thenReturn(uri);

  if (body != null) {
    final jsonBody = jsonEncode(body);
    when(() => request.body()).thenAnswer((_) async => jsonBody);
    when(() => request.json()).thenAnswer((_) async => body);
  } else {
    when(() => request.body()).thenAnswer((_) async => '');
  }

  // Configure Context
  when(() => context.request).thenReturn(request);

  return context;
}

// test/middleware/auth_middleware_test.dart
import 'package:dart_frog/dart_frog.dart';
import 'package:mocktail/mocktail.dart';
import 'package:test/test.dart';

import '../../routes/_middleware.dart';
import '../helpers/mock_context.dart';

void main() {
  group('authMiddleware', () {
    late Handler mockNextHandler;

    setUp(() {
      mockNextHandler = (_) async => Response(body: 'success');
    });

    test('returns 401 when no authorization header', () async {
      final context = createMockContext(headers: {});

      final middleware = authMiddleware(mockNextHandler);
      final response = await middleware(context);

      expect(response.statusCode, equals(401));
      expect(await response.body(), equals('Unauthorized'));
    });

    test('returns 401 when authorization is not Bearer', () async {
      final context = createMockContext(
        headers: {'authorization': 'Basic abc123'},
      );

      final middleware = authMiddleware(mockNextHandler);
      final response = await middleware(context);

      expect(response.statusCode, equals(401));
    });

    test('returns 403 when token is invalid', () async {
      final context = createMockContext(
        headers: {'authorization': 'Bearer invalid_token'},
      );

      final middleware = authMiddleware(mockNextHandler);
      final response = await middleware(context);

      expect(response.statusCode, equals(403));
    });

    test('calls next handler when token is valid', () async {
      var handlerCalled = false;
      final testHandler = (RequestContext ctx) async {
        handlerCalled = true;
        return Response(body: 'protected content');
      };

      final context = createMockContext(
        headers: {'authorization': 'Bearer valid_test_token'},
      );

      final middleware = authMiddleware(testHandler);
      await middleware(context);

      expect(handlerCalled, isTrue);
    });
  });
}

