      });

      test('returns 400 when name missing', () async {
        when(() => request.json()).thenAnswer((_) async => {'price': 29.99});
        // Verify 400 response
      });
    });

    group('DELETE /products/:id', () {
      setUp(() {
        when(() => request.method).thenReturn(HttpMethod.delete);
      });

      test('deletes product and returns 204', () async {
        when(() => repository.delete('123')).thenAnswer((_) async {});
        // Verify 204 response
      });

      test('returns 404 when product not found', () async {
        when(() => repository.delete('nonexistent'))
            .thenThrow(Exception('Not found'));
        // Verify 404 response
      });
    });
  });
}

