import 'package:flutter/material.dart';
import 'database_helper.dart';

class SQLiteDemo extends StatefulWidget {
  @override
  State<SQLiteDemo> createState() => _SQLiteDemoState();
}

class _SQLiteDemoState extends State<SQLiteDemo> {
  final DatabaseHelper _db = DatabaseHelper();
  List<Map<String, dynamic>> _notes = [];
  final _titleController = TextEditingController();
  final _contentController = TextEditingController();

  @override
  void initState() {
    super.initState();
    _loadNotes();
  }

  Future<void> _loadNotes() async {
    final notes = await _db.getAllNotes();
    setState(() {
      _notes = notes;
    });
  }

  Future<void> _addNote() async {
    if (_titleController.text.isEmpty) return;

    await _db.insertNote({
      'title': _titleController.text,
      'content': _contentController.text,
      'created_at': DateTime.now().millisecondsSinceEpoch,
    });

    _titleController.clear();
    _contentController.clear();
    _loadNotes();

    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(content: Text('Note added!')),
    );
  }

  Future<void> _deleteNote(int id) async {
    await _db.deleteNote(id);
    _loadNotes();

    ScaffoldMessenger.of(context).showSnackBar(
      SnackBar(content: Text('Note deleted')),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: Text('SQLite Demo (${_notes.length} notes)')),
      body: Column(
        children: [
          // Input form
          Padding(
            padding: EdgeInsets.all(16),
            child: Column(
              children: [
                TextField(
                  controller: _titleController,
                  decoration: InputDecoration(labelText: 'Title'),
                ),
                TextField(
                  controller: _contentController,
                  decoration: InputDecoration(labelText: 'Content'),
                  maxLines: 3,
                ),
                SizedBox(height: 10),
                ElevatedButton(
                  onPressed: _addNote,
                  child: Text('Add Note'),
                ),
              ],
            ),
          ),

          Divider(),

          // Notes list
          Expanded(
            child: _notes.isEmpty
                ? Center(child: Text('No notes yet!'))
                : ListView.builder(
                    itemCount: _notes.length,
                    itemBuilder: (context, index) {
                      final note = _notes[index];
                      final createdAt = DateTime.fromMillisecondsSinceEpoch(
                        note['created_at'],
                      );

                      return ListTile(
                        title: Text(note['title']),
                        subtitle: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(note['content'] ?? ''),
                            SizedBox(height: 4),
                            Text(
                              'Created: ${createdAt.toString().split('.')[0]}',
                              style: TextStyle(fontSize: 12, color: Colors.grey),
                            ),
                          ],
                        ),
                        trailing: IconButton(
                          icon: Icon(Icons.delete, color: Colors.red),
                          onPressed: () => _deleteNote(note['id']),
                        ),
                      );
                    },
                  ),
          ),
        ],
      ),
    );
  }

  @override
  void dispose() {
    _titleController.dispose();
    _contentController.dispose();
    super.dispose();
  }
}

