// State class
@immutable
class TodosState {
  const TodosState({this.todos = const []});
  final List<Todo> todos;

  TodosState copyWith({List<Todo>? todos}) {
    return TodosState(todos: todos ?? this.todos);
  }
}

// Notifier class
class TodosNotifier extends Notifier<TodosState> {
  @override
  TodosState build() => const TodosState();

  void addTodo(String title) {
    final newTodo = Todo(id: uuid.v4(), title: title);
    state = state.copyWith(todos: [...state.todos, newTodo]);
  }

  void toggleTodo(String id) {
    state = state.copyWith(
      todos: state.todos.map((todo) {
        if (todo.id == id) {
          return todo.copyWith(completed: !todo.completed);
        }
        return todo;
      }).toList(),
    );
  }

  void removeTodo(String id) {
    state = state.copyWith(
      todos: state.todos.where((t) => t.id != id).toList(),
    );
  }
}

// Provider definition
