// Custom debounce hook
T? useDebounced<T>(T value, Duration delay) {
  final debouncedValue = useState<T?>(null);

  useEffect(() {
    final timer = Timer(delay, () => debouncedValue.value = value);
    return timer.cancel;
  }, [value, delay]);

  return debouncedValue.value;
}

