@freezed
class Todo with _$Todo {
  const factory Todo({
    required String id,
    required String title,
    @Default(false) bool completed,
  }) = _Todo;
}

// providers/todos_provider.dart
@riverpod
class Todos extends _$Todos {
  @override
  Future<List<Todo>> build() async {
    // Simulate API fetch
    await Future.delayed(const Duration(seconds: 1));
    return [
      const Todo(id: '1', title: 'Learn Riverpod'),
      const Todo(id: '2', title: 'Build awesome apps'),
    ];
  }

  void addTodo(String title) {
    final newTodo = Todo(id: const Uuid().v4(), title: title);
    state = AsyncData([...state.value ?? [], newTodo]);
  }

  void toggleTodo(String id) {
    state = AsyncData(
      state.value?.map((t) => t.id == id ? t.copyWith(completed: !t.completed) : t).toList() ?? [],
    );
  }

  void deleteTodo(String id) {
    state = AsyncData(
      state.value?.where((t) => t.id != id).toList() ?? [],
    );
  }
}

// providers/filter_provider.dart
enum TodoFilter { all, active, completed }

@riverpod
class Filter extends _$Filter {
  @override
  TodoFilter build() => TodoFilter.all;

  void setFilter(TodoFilter filter) => state = filter;
}

// providers/filtered_todos.dart
@riverpod
List<Todo> filteredTodos(FilteredTodosRef ref) {
  final filter = ref.watch(filterProvider);
  final todosAsync = ref.watch(todosProvider);

  return todosAsync.when(
    data: (todos) => switch (filter) {
      TodoFilter.all => todos,
      TodoFilter.active => todos.where((t) => !t.completed).toList(),
      TodoFilter.completed => todos.where((t) => t.completed).toList(),
    },
    loading: () => [],
    error: (_, __) => [],
  );
}

