// lib/database/daos/task_dao.dart
import 'package:drift/drift.dart';
import '../database.dart';
import '../tables.dart';

part 'task_dao.g.dart';

@DriftAccessor(tables: [Tasks, Categories])
class TaskDao extends DatabaseAccessor<AppDatabase> with _$TaskDaoMixin {
  TaskDao(AppDatabase db) : super(db);

  // CREATE
  Future<int> insertTask(TasksCompanion task) {
    return into(tasks).insert(task);
  }

  // READ - Get all tasks
  Future<List<Task>> getAllTasks() {
    return select(tasks).get();
  }

  // READ - Get single task
  Future<Task?> getTaskById(int id) {
    return (select(tasks)..where((t) => t.id.equals(id))).getSingleOrNull();
  }

  // READ - Get incomplete tasks
  Future<List<Task>> getIncompleteTasks() {
    return (select(tasks)
      ..where((t) => t.completed.equals(false))
      ..orderBy([(t) => OrderingTerm.asc(t.dueDate)]))
      .get();
  }

  // UPDATE
  Future<bool> updateTask(Task task) {
    return update(tasks).replace(task);
  }

  // UPDATE - Toggle completion
  Future<int> toggleComplete(int id, bool completed) {
    return (update(tasks)..where((t) => t.id.equals(id)))
      .write(TasksCompanion(completed: Value(completed)));
  }

  // DELETE
  Future<int> deleteTask(int id) {
    return (delete(tasks)..where((t) => t.id.equals(id))).go();
  }
}

// Join tasks with categories
class TaskWithCategory {
  final Task task;
  final Category? category;

  TaskWithCategory(this.task, this.category);
}

// In TaskDao:
Future<List<TaskWithCategory>> getTasksWithCategories() {
  final query = select(tasks).join([
    leftOuterJoin(categories, categories.id.equalsExp(tasks.categoryId)),
  ]);

  return query.map((row) {
    return TaskWithCategory(
      row.readTable(tasks),
      row.readTableOrNull(categories),
    );
  }).get();
}

// Filter by category
Future<List<Task>> getTasksByCategory(int categoryId) {
  return (select(tasks)
    ..where((t) => t.categoryId.equals(categoryId)))
    .get();
}

// Count tasks per category
Future<List<CategoryCount>> getTaskCountByCategory() {
  final count = tasks.id.count();

  final query = selectOnly(categories)
    ..addColumns([categories.name, count])
    ..join([leftOuterJoin(tasks, tasks.categoryId.equalsExp(categories.id))])
    ..groupBy([categories.id]);

  return query.map((row) {
    return CategoryCount(
      name: row.read(categories.name)!,
      count: row.read(count)!,
    );
  }).get();
}

// In TaskDao:

// Stream of all tasks - emits whenever data changes
Stream<List<Task>> watchAllTasks() {
  return select(tasks).watch();
}

// Stream of incomplete tasks
Stream<List<Task>> watchIncompleteTasks() {
  return (select(tasks)
    ..where((t) => t.completed.equals(false))
    ..orderBy([(t) => OrderingTerm.asc(t.dueDate)]))
    .watch();
}

// Stream of single task
Stream<Task?> watchTask(int id) {
  return (select(tasks)..where((t) => t.id.equals(id)))
    .watchSingleOrNull();
}

// Usage in Flutter widget:
class TaskListWidget extends StatelessWidget {
  final TaskDao taskDao;

  @override
  Widget build(BuildContext context) {
    return StreamBuilder<List<Task>>(
      stream: taskDao.watchIncompleteTasks(),
      builder: (context, snapshot) {
        if (!snapshot.hasData) {
          return const CircularProgressIndicator();
        }

        final tasks = snapshot.data!;
        return ListView.builder(
          itemCount: tasks.length,
          itemBuilder: (context, index) {
            final task = tasks[index];
            return TaskTile(task: task);
          },
        );
      },
    );
  }
}

