// lib/database/database.dart
@DriftDatabase(tables: [Tasks, Categories])
class AppDatabase extends _$AppDatabase {
  AppDatabase() : super(_openConnection());

  // Increment this when you change the schema
  @override
  int get schemaVersion => 2; // Was 1, now 2

  @override
  MigrationStrategy get migration {
    return MigrationStrategy(
      // Called when creating a fresh database
      onCreate: (Migrator m) async {
        await m.createAll();
      },

      // Called when upgrading from old version
      onUpgrade: (Migrator m, int from, int to) async {
        // Run migrations for each version
        if (from < 2) {
          // Migration from version 1 to 2
          await m.addColumn(tasks, tasks.priority);
        }
        if (from < 3) {
          // Migration from version 2 to 3
          await m.createTable(tags);
        }
      },

      // Called after opening (useful for seeding data)
      beforeOpen: (details) async {
        // Enable foreign keys
        await customStatement('PRAGMA foreign_keys = ON');

        // Seed default categories if needed
        if (details.wasCreated) {
          await _seedDefaultCategories();
        }
      },
    );
  }

  Future<void> _seedDefaultCategories() async {
    await into(categories).insert(CategoriesCompanion(
      name: const Value('Work'),
      color: Value(Colors.blue.value),
    ));
    await into(categories).insert(CategoriesCompanion(
      name: const Value('Personal'),
      color: Value(Colors.green.value),
    ));
  }
}

// Common onUpgrade patterns:

onUpgrade: (Migrator m, int from, int to) async {
  // 1. ADD A COLUMN
  if (from < 2) {
    await m.addColumn(tasks, tasks.priority);
  }

  // 2. CREATE A NEW TABLE
  if (from < 3) {
    await m.createTable(tags);
  }

  // 3. DROP A TABLE
  if (from < 4) {
    await m.deleteTable('old_table_name');
  }

  // 4. RENAME A COLUMN (requires custom SQL)
  if (from < 5) {
    await customStatement(
      'ALTER TABLE tasks RENAME COLUMN old_name TO new_name'
    );
  }

  // 5. COMPLEX MIGRATION (data transformation)
  if (from < 6) {
    // Add new column
    await m.addColumn(tasks, tasks.statusEnum);

    // Migrate data from old column to new
    await customStatement('''
      UPDATE tasks 
      SET status_enum = CASE 
        WHEN completed = 1 THEN 'done'
        ELSE 'pending'
      END
    ''');

    // Optionally drop old column (SQLite limitation: can't drop columns easily)
  }
},

