enum ConflictStrategy {
  /// Server always wins - simplest
  serverWins,

  /// Client always wins - risky
  clientWins,

  /// Most recent change wins
  lastWriteWins,

  /// Keep both versions for user to resolve
  keepBoth,

  /// Merge field by field
  fieldMerge,
}

// Example: Last-write-wins resolution
class ConflictResolver {
  T resolve<T extends Syncable>(T local, T remote) {
    // Compare timestamps
    if (local.updatedAt.isAfter(remote.updatedAt)) {
      return local;
    } else {
      return remote;
    }
  }
}

// Example: Field-level merge for notes
Note mergeNotes(Note local, Note remote) {
  return Note(
    id: local.id,
    // Use most recently updated title
    title: local.titleUpdatedAt.isAfter(remote.titleUpdatedAt)
        ? local.title
        : remote.title,
    // Use most recently updated content
    content: local.contentUpdatedAt.isAfter(remote.contentUpdatedAt)
        ? local.content
        : remote.content,
    // Keep latest timestamp
    updatedAt: local.updatedAt.isAfter(remote.updatedAt)
        ? local.updatedAt
        : remote.updatedAt,
  );
}

