@collection
class SyncOperation {
  Id id = Isar.autoIncrement;

  @Index()
  String entityType = ''; // 'note', 'task', etc.

  int entityId = 0;

  @Enumerated(EnumType.ordinal)
  SyncOperationType operation = SyncOperationType.create;

  DateTime createdAt = DateTime.now();

  @Index()
