import 'package:connectivity_plus/connectivity_plus.dart';
import 'dart:async';

class ConnectivityService {
  final _connectivity = Connectivity();
  final _controller = StreamController<bool>.broadcast();

  Stream<bool> get onConnectivityChanged => _controller.stream;
  bool _isOnline = true;
  bool get isOnline => _isOnline;

  ConnectivityService() {
    _connectivity.onConnectivityChanged.listen(_updateStatus);
    _checkInitialStatus();
  }

  Future<void> _checkInitialStatus() async {
    final result = await _connectivity.checkConnectivity();
    _updateStatus(result);
  }

  void _updateStatus(ConnectivityResult result) {
    final online = result != ConnectivityResult.none;
    if (online != _isOnline) {
      _isOnline = online;
      _controller.add(_isOnline);
    }
  }

  void dispose() {
    _controller.close();
  }
}

// Usage in sync service
class SyncService {
  final ConnectivityService _connectivity;

  void startAutoSync() {
    _connectivity.onConnectivityChanged.listen((isOnline) {
      if (isOnline) {
        sync(); // Sync when connection restored
      }
    });
  }
}

// lib/sync/sync_service.dart
class SyncService {
  final Isar isar;
  final ApiClient api;
  final ConnectivityService connectivity;

  DateTime? lastSyncAt;
  bool _isSyncing = false;

  SyncService(this.isar, this.api, this.connectivity);

  Future<SyncResult> sync() async {
    if (_isSyncing) return SyncResult.alreadySyncing;
    if (!connectivity.isOnline) return SyncResult.offline;

    _isSyncing = true;

    try {
      // 1. PUSH: Upload local changes
      await _pushChanges();

      // 2. PULL: Download remote changes
      await _pullChanges();

      lastSyncAt = DateTime.now();
      return SyncResult.success;
    } catch (e) {
      return SyncResult.error;
    } finally {
      _isSyncing = false;
    }
  }

  Future<void> _pushChanges() async {
    // Get unsynced operations
    final operations = await isar.syncOperations
        .where()
        .isSyncedEqualTo(false)
        .findAll();

    for (final op in operations) {
      try {
        switch (op.operation) {
          case SyncOperationType.create:
          case SyncOperationType.update:
            final note = await isar.notes.get(op.entityId);
            if (note != null) {
              await api.upsertNote(note.toJson());
            }
            break;
          case SyncOperationType.delete:
            await api.deleteNote(op.entityId);
            break;
        }

        // Mark as synced
        await isar.writeTxn(() async {
          op.isSynced = true;
          await isar.syncOperations.put(op);
        });
      } catch (e) {
        // Increment retry count
        await isar.writeTxn(() async {
          op.retryCount++;
          await isar.syncOperations.put(op);
        });
      }
    }
  }

  Future<void> _pullChanges() async {
    // Fetch changes since last sync
    final remoteNotes = await api.getNotesSince(lastSyncAt);

    await isar.writeTxn(() async {
      for (final remoteNote in remoteNotes) {
        final localNote = await isar.notes.get(remoteNote.id);

        if (localNote == null) {
          // New note from server
          await isar.notes.put(remoteNote);
        } else {
          // Conflict - resolve with last-write-wins
          if (remoteNote.updatedAt.isAfter(localNote.updatedAt)) {
            await isar.notes.put(remoteNote);
          }
          // Otherwise keep local (it will push on next sync)
        }
      }
    });
  }
}

enum SyncResult {
  success,
  offline,
  alreadySyncing,
  error,
}

