class SyncService {
  final Isar isar;
  final MockApiClient api;

  bool _isSyncing = false;
  DateTime? lastSyncAt;

  SyncService(this.isar, this.api);

  Future<bool> sync() async {
    if (_isSyncing) return false;

    _isSyncing = true;
    try {
      await _pushChanges();
      await _pullChanges();
      lastSyncAt = DateTime.now();
      return true;
    } catch (e) {
      print('Sync error: $e');
      return false;
    } finally {
      _isSyncing = false;
    }
  }

  Future<void> _pushChanges() async {
    final unsynced = await isar.syncOperations
        .where()
        .isSyncedEqualTo(false)
        .findAll();

    for (final op in unsynced) {
      try {
        if (op.operation == SyncOperationType.delete) {
          await api.delete('notes', op.entityId);
        } else {
          final note = await isar.notes.get(op.entityId);
          if (note != null) {
            await api.upsert('notes', note.toJson());
          }
        }

        await isar.writeTxn(() async {
          op.isSynced = true;
          await isar.syncOperations.put(op);
        });
      } catch (e) {
        await isar.writeTxn(() async {
          op.retryCount++;
          await isar.syncOperations.put(op);
        });
      }
    }
  }

  Future<void> _pullChanges() async {
    final remoteNotes = await api.fetchSince('notes', lastSyncAt);

    await isar.writeTxn(() async {
      for (final remote in remoteNotes) {
        final local = await isar.notes.get(remote.id);

        // Last-write-wins conflict resolution
        if (local == null || remote.updatedAt.isAfter(local.updatedAt)) {
          await isar.notes.put(remote);
        }
      }
    });
  }
}

