import 'package:flutter/material.dart';
import 'package:isar/isar.dart';

class NotesScreen extends StatelessWidget {
  final NoteRepository noteRepo;
  final SyncService syncService;
  final ConnectivityService connectivity;

  const NotesScreen({
    required this.noteRepo,
    required this.syncService,
    required this.connectivity,
  });

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('My Notes'),
        actions: [
          // Sync status indicator
          StreamBuilder<bool>(
            stream: connectivity.onConnectivityChanged,
            initialData: connectivity.isOnline,
            builder: (context, snapshot) {
              final isOnline = snapshot.data ?? false;
              return IconButton(
                icon: Icon(
                  isOnline ? Icons.cloud_done : Icons.cloud_off,
                  color: isOnline ? Colors.green : Colors.grey,
                ),
                onPressed: isOnline ? () => syncService.sync() : null,
                tooltip: isOnline ? 'Synced' : 'Offline',
              );
            },
          ),
        ],
      ),
      body: RefreshIndicator(
        onRefresh: () => syncService.sync(),
        child: StreamBuilder<List<Note>>(
          stream: noteRepo.watchAllNotes(),
          builder: (context, snapshot) {
            if (!snapshot.hasData) {
              return const Center(child: CircularProgressIndicator());
            }

            final notes = snapshot.data!;

            if (notes.isEmpty) {
              return const Center(
                child: Text('No notes yet. Tap + to create one!'),
              );
            }

            return ListView.builder(
              itemCount: notes.length,
              itemBuilder: (context, index) {
                final note = notes[index];
                return NoteCard(
                  note: note,
                  onTap: () => _openNote(context, note),
                  onDelete: () => _deleteNote(note),
                );
              },
            );
          },
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () => _createNote(context),
        child: const Icon(Icons.add),
      ),
    );
  }

  void _createNote(BuildContext context) {
    Navigator.push(
      context,
      MaterialPageRoute(
        builder: (_) => NoteEditorScreen(
          noteRepo: noteRepo,
        ),
      ),
    );
  }

  void _openNote(BuildContext context, Note note) {
    Navigator.push(
      context,
      MaterialPageRoute(
        builder: (_) => NoteEditorScreen(
          noteRepo: noteRepo,
          note: note,
        ),
      ),
    );
  }

  Future<void> _deleteNote(Note note) async {
    await noteRepo.deleteNote(note.id);
  }
}

// lib/services/note_sync_service.dart
class NoteSyncService {
  final Isar isar;
  final NotesApi api;
  final ConnectivityService connectivity;

  bool _isSyncing = false;
  DateTime? _lastSyncAt;

  NoteSyncService(this.isar, this.api, this.connectivity) {
    // Auto-sync when coming online
    connectivity.onConnectivityChanged.listen((isOnline) {
      if (isOnline) sync();
    });
  }

  Future<void> sync() async {
    if (_isSyncing || !connectivity.isOnline) return;
    _isSyncing = true;

    try {
      // 1. Push local changes
      final pending = await isar.syncOperations
          .where()
          .isPendingEqualTo(true)
          .findAll();

      for (final op in pending) {
        await _processSyncOp(op);
      }

      // 2. Pull remote changes
      final remoteNotes = await api.getNotesSince(_lastSyncAt);
      await _mergeRemoteNotes(remoteNotes);

      _lastSyncAt = DateTime.now();
    } finally {
      _isSyncing = false;
    }
  }

  Future<void> _processSyncOp(SyncOperation op) async {
    try {
      final note = await isar.notes.get(op.noteId);
      if (note == null) return;

      switch (op.type) {
        case SyncOpType.create:
        case SyncOpType.update:
          final serverNote = await api.upsertNote(note.toJson());
          await isar.writeTxn(() async {
            note.serverId = serverNote['id'];
            await isar.notes.put(note);
          });
          break;
        case SyncOpType.delete:
          if (note.serverId != null) {
            await api.deleteNote(note.serverId!);
          }
          break;
      }

      // Mark operation as complete
      await isar.writeTxn(() async {
        op.isPending = false;
        await isar.syncOperations.put(op);
      });
    } catch (e) {
      // Will retry on next sync
      print('Sync error: $e');
    }
  }

  Future<void> _mergeRemoteNotes(List<Map<String, dynamic>> remoteNotes) async {
    await isar.writeTxn(() async {
      for (final json in remoteNotes) {
        final remote = Note.fromJson(json);

        // Find local note by serverId
        final local = await isar.notes
            .where()
            .serverIdEqualTo(remote.serverId)
            .findFirst();

        if (local == null) {
          // New note from server
          await isar.notes.put(remote);
        } else if (remote.updatedAt.isAfter(local.updatedAt)) {
          // Server version is newer
          remote.id = local.id; // Keep local ID
          await isar.notes.put(remote);
        }
        // If local is newer, it will push on next sync
      }
    });
  }
}

