import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;

public class APIExample {
    public static void main(String[] args) throws Exception {
        // 1. Create HttpClient
        HttpClient client = HttpClient.newHttpClient();

        // 2. Build request
        HttpRequest request = HttpRequest.newBuilder()
            .uri(URI.create("https://api.example.com/users/123"))
            .GET()
            .build();

        // 3. Send request and get response
        HttpResponse<String> response = client.send(
            request,
            HttpResponse.BodyHandlers.ofString()
        );

        // 4. Process response
        System.out.println("Status: " + response.statusCode());
        System.out.println("Body: " + response.body());
    }
}

import com.google.gson.Gson;

// Create data object
class User {
    String name;
    int age;

    User(String name, int age) {
        this.name = name;
        this.age = age;
    }
}

// Convert to JSON
Gson gson = new Gson();
User newUser = new User("Alice", 20);
String json = gson.toJson(newUser);  // {"name":"Alice","age":20}

// Build POST request
HttpRequest request = HttpRequest.newBuilder()
    .uri(URI.create("https://api.example.com/users"))
    .header("Content-Type", "application/json")
    .POST(HttpRequest.BodyPublishers.ofString(json))
    .build();

// Send request
HttpResponse<String> response = client.send(
    request,
    HttpResponse.BodyHandlers.ofString()
);

// Parse response
if (response.statusCode() == 201) {
    User created = gson.fromJson(response.body(), User.class);
    System.out.println("Created user: " + created.name);
}

ALWAYS handle errors:

try {
    HttpResponse<String> response = client.send(request, ...);

    // Check status code
    if (response.statusCode() == 200) {
        // Success!
        String body = response.body();
    } else if (response.statusCode() == 404) {
        System.err.println("Resource not found");
    } else if (response.statusCode() >= 500) {
        System.err.println("Server error: " + response.statusCode());
    } else {
        System.err.println("Error: " + response.statusCode());
    }

} catch (IOException e) {
    // Network error (can't reach server)
    System.err.println("Network error: " + e.getMessage());
} catch (InterruptedException e) {
    // Request was interrupted
    System.err.println("Request interrupted");
}

COMMON ERRORS:
- IOException: Network issues, timeout
- 4xx codes: Client problems (bad request, not found)
- 5xx codes: Server problems (crash, overload)

