// API Configuration
const API_BASE_URL = 'http://localhost:8080/api';

// Get auth credentials from localStorage
function getAuthHeader() {
    const email = localStorage.getItem('userEmail');
    const password = localStorage.getItem('userPassword');

    if (!email || !password) {
        return null;
    }

    // Basic Auth: base64(email:password)
    const credentials = btoa(`${email}:${password}`);
    return `Basic ${credentials}`;
}

// Generic API request function (2024 best practice)
async function apiRequest(endpoint, options = {}) {
    const authHeader = getAuthHeader();

    const config = {
        headers: {
            'Content-Type': 'application/json',
            ...(authHeader && { 'Authorization': authHeader }),
            ...options.headers
        },
        ...options
    };

    try {
        const response = await fetch(`${API_BASE_URL}${endpoint}`, config);

        // Check for authentication errors
        if (response.status === 401) {
            localStorage.clear();
            window.location.href = '/login.html';
            throw new Error('Authentication failed');
        }

        // Parse response
        const data = response.status !== 204 ? await response.json() : null;

        if (!response.ok) {
            throw new Error(data?.detail || data?.message || 'Request failed');
        }

        return data;
    } catch (error) {
        console.error('API Error:', error);
        throw error;
    }
}

// Check if user is authenticated
function isAuthenticated() {
    return localStorage.getItem('userEmail') !== null;
}

// Redirect to login if not authenticated
function requireAuth() {
    if (!isAuthenticated()) {
        window.location.href = '/login.html';
    }
}

MODERN JAVASCRIPT FEATURES:
- async/await (ES2017) - Clean asynchronous code
- Template literals (`${var}`) - String interpolation
- Arrow functions (() => {}) - Concise syntax
- Destructuring ({...options}) - Spread operator
- Optional chaining (data?.detail) - Null safety

