// Load all tasks
async function loadTasks() {
    try {
        const tasks = await apiRequest('/tasks');
        displayTasks(tasks);
    } catch (error) {
        showMessage('Failed to load tasks', 'error');
    }
}

// Display tasks in UI
function displayTasks(tasks) {
    const container = document.getElementById('tasks');

    if (tasks.length === 0) {
        container.innerHTML = '<p>No tasks yet. Create one above!</p>';
        return;
    }

    // Modern template literals and map
    container.innerHTML = tasks.map(task => `
        <div class="task-card ${task.completed ? 'completed' : ''}">
            <div class="task-content">
                <h3>${escapeHtml(task.title)}</h3>
                <p>${escapeHtml(task.description || '')}</p>
                <small>Created: ${new Date(task.createdAt).toLocaleDateString()}</small>
            </div>
            <div class="task-actions">
                <button onclick="toggleComplete(${task.id}, ${!task.completed})" 
                        class="btn ${task.completed ? 'btn-secondary' : 'btn-success'}">
                    ${task.completed ? 'Undo' : 'Complete'}
                </button>
                <button onclick="deleteTask(${task.id})" class="btn btn-danger">
                    Delete
                </button>
            </div>
        </div>
    `).join('');
}

// Create new task
async function createTask(event) {
    event.preventDefault();

    const title = document.getElementById('title').value;
    const description = document.getElementById('description').value;

    try {
        await apiRequest('/tasks', {
            method: 'POST',
            body: JSON.stringify({ title, description })
        });

        // Clear form
        document.getElementById('createTaskForm').reset();

        showMessage('Task created!', 'success');
        await loadTasks();  // Reload tasks

    } catch (error) {
        showMessage(error.message, 'error');
    }
}

// Toggle task completion
async function toggleComplete(taskId, completed) {
    try {
        await apiRequest(`/tasks/${taskId}`, {
            method: 'PUT',
            body: JSON.stringify({ completed })
        });

        await loadTasks();
    } catch (error) {
        showMessage(error.message, 'error');
    }
}

// Delete task
async function deleteTask(taskId) {
    if (!confirm('Delete this task?')) return;

    try {
        await apiRequest(`/tasks/${taskId}`, {
            method: 'DELETE'
        });

        showMessage('Task deleted', 'success');
        await loadTasks();
    } catch (error) {
        showMessage(error.message, 'error');
    }
}

// Prevent XSS attacks
function escapeHtml(text) {
    const div = document.createElement('div');
    div.textContent = text;
    return div.innerHTML;
}

