import java.util.List;
import static org.assertj.core.api.Assertions.assertThat;

@DataJpaTest  // Configures in-memory database
class TaskRepositoryTest {

    @Autowired
    private TaskRepository taskRepository;

    @Autowired
    private TestEntityManager entityManager;

    private User testUser;

    @BeforeEach
    void setUp() {
        // Create test user
        testUser = new User();
        testUser.setEmail("test@email.com");
        testUser.setPassword("password");
        testUser.setName("Test User");
        entityManager.persist(testUser);
        entityManager.flush();
    }

    @Test
    void shouldSaveTask() {
        // Given
        Task task = new Task("Buy milk", "From store", testUser);

        // When
        Task saved = taskRepository.save(task);

        // Then
        assertThat(saved.getId()).isNotNull();
        assertThat(saved.getTitle()).isEqualTo("Buy milk");
        assertThat(saved.getCompleted()).isFalse();
    }

    @Test
    void shouldFindTasksByUserId() {
        // Given
        Task task1 = new Task("Task 1", "Desc 1", testUser);
        Task task2 = new Task("Task 2", "Desc 2", testUser);
        entityManager.persist(task1);
        entityManager.persist(task2);
        entityManager.flush();

        // When
        List<Task> tasks = taskRepository.findByUserId(testUser.getId());

        // Then
        assertThat(tasks).hasSize(2);
        assertThat(tasks).extracting(Task::getTitle)
            .containsExactlyInAnyOrder("Task 1", "Task 2");
    }

    @Test
    void shouldCountIncompleteTasks() {
        // Given
        Task completed = new Task("Done", "Finished", testUser);
        completed.setCompleted(true);
        Task incomplete = new Task("Todo", "Not done", testUser);
        entityManager.persist(completed);
        entityManager.persist(incomplete);
        entityManager.flush();

        // When
        Long count = taskRepository.countByUserIdAndCompletedFalse(testUser.getId());

        // Then
        assertThat(count).isEqualTo(1);
    }
}

