import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;
import java.util.Optional;
import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.assertThatThrownBy;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.Mockito.*;

@ExtendWith(MockitoExtension.class)
class TaskServiceTest {

    @Mock
    private TaskRepository taskRepository;

    @InjectMocks
    private TaskService taskService;

    @Test
    void shouldCreateTask() {
        // Given
        TaskRequest request = new TaskRequest();
        request.setTitle("Buy milk");
        request.setDescription("From store");

        User user = new User();
        user.setId(1L);

        Task savedTask = new Task("Buy milk", "From store", user);
        savedTask.setId(1L);

        when(taskRepository.save(any(Task.class))).thenReturn(savedTask);

        // When
        TaskDTO result = taskService.createTask(request, 1L);

        // Then
        assertThat(result.getTitle()).isEqualTo("Buy milk");
        assertThat(result.getCompleted()).isFalse();
        verify(taskRepository).save(any(Task.class));
    }

    @Test
    void shouldThrowExceptionWhenTaskNotFound() {
        // Given
        when(taskRepository.findById(999L)).thenReturn(Optional.empty());

        // When/Then
        assertThatThrownBy(() -> taskService.getTaskById(999L, 1L))
            .isInstanceOf(ResourceNotFoundException.class)
            .hasMessageContaining("Task not found");
    }

    @Test
    void shouldThrowExceptionWhenUserNotOwner() {
        // Given
        User owner = new User();
        owner.setId(1L);

        Task task = new Task("Task", "Desc", owner);
        task.setId(1L);

        when(taskRepository.findById(1L)).thenReturn(Optional.of(task));

        // When/Then
        assertThatThrownBy(() -> taskService.getTaskById(1L, 2L))  // Different user!
            .isInstanceOf(ForbiddenException.class)
            .hasMessageContaining("permission");
    }
}

