# Password Validator with Custom Exceptions
# This solution demonstrates creating and raising custom exceptions

# Step 1: Define custom exception classes
class TooShortError(Exception):
    """Password is too short."""
    pass

class NoDigitError(Exception):
    """Password has no digits."""
    pass

class NoUppercaseError(Exception):
    """Password has no uppercase letters."""
    pass

def validate_password(password):
    """Validate password and raise exceptions on failure."""

    # Step 2: Check length (must be at least 8 characters)
    if len(password) < 8:
        raise TooShortError(f"Password must be at least 8 characters (got {len(password)})")

    # Step 3: Check for at least one digit
    if not any(char.isdigit() for char in password):
        raise NoDigitError("Password must contain at least one digit")

    # Step 4: Check for at least one uppercase letter
    if not any(char.isupper() for char in password):
        raise NoUppercaseError("Password must contain at least one uppercase letter")

    # All checks passed!
    return True

# Test cases
test_passwords = [
    "SecurePass123",  # Valid
    "short",          # Too short
    "nouppercase123", # No uppercase
    "NODIGITS",       # No digit
]

for pwd in test_passwords:
    try:
        if validate_password(pwd):
            print(f"Valid: '{pwd}'")
    except (TooShortError, NoDigitError, NoUppercaseError) as e:
        print(f"Invalid '{pwd}': {e}")

# User Registration Validator
# This solution demonstrates comprehensive input validation

def validate_registration(username, email, age, password):
    """Validate user registration data."""

    # Step 1: Validate username
    if not username.strip():
        raise ValueError("Username cannot be empty")
    if not 3 <= len(username) <= 20:
        raise ValueError(f"Username must be 3-20 characters (got {len(username)})")
    if not username.isalnum():
        raise ValueError("Username must contain only letters and numbers")

    # Step 2: Validate email
    if not email.strip():
        raise ValueError("Email cannot be empty")
    if '@' not in email:
        raise ValueError("Email must contain @")
    # Check for . after @
    at_index = email.index('@')
    if '.' not in email[at_index:]:
        raise ValueError("Email must contain . after @")

    # Step 3: Validate age
    if not isinstance(age, int):
        raise TypeError(f"Age must be an integer, got {type(age).__name__}")
    if not 13 <= age <= 120:
        raise ValueError(f"Age must be between 13-120 (got {age})")

    # Step 4: Validate password
    if not password:
        raise ValueError("Password cannot be empty")
    if len(password) < 8:
        raise ValueError(f"Password must be at least 8 characters (got {len(password)})")

    return True

# Test cases
test_cases = [
    ("alice123", "alice@example.com", 25, "SecurePass123"),  # Valid
    ("", "alice@example.com", 25, "SecurePass123"),  # Empty username
    ("ab", "alice@example.com", 25, "SecurePass123"),  # Short username
    ("alice123", "invalid-email", 25, "SecurePass123"),  # Invalid email
    ("alice123", "alice@example.com", 10, "SecurePass123"),  # Too young
    ("alice123", "alice@example.com", 25, "short"),  # Short password
]

for i, (user, email, age, pwd) in enumerate(test_cases, 1):
    print(f"Test {i}: user='{user}', email='{email}', age={age}")
    try:
        if validate_registration(user, email, age, pwd):
            print("  Registration valid")
    except (ValueError, TypeError) as e:
        print(f"  Error: {e}")

from datetime import datetime
from collections import defaultdict

# Advanced Log Analyzer
# This solution demonstrates time-based analysis with datetime

def parse_timestamp(timestamp_str):
    """Parse timestamp string to datetime object."""
    try:
        return datetime.strptime(timestamp_str, '%Y-%m-%d %H:%M:%S')
    except ValueError:
        return None

def group_by_hour(entries):
    """Group log entries by hour."""
    # Use defaultdict for automatic list creation
    grouped = defaultdict(list)

    for entry in entries:
        # Parse the timestamp
        dt = parse_timestamp(entry['timestamp'])
        if dt:
            # Extract hour and group
            hour = dt.hour
            grouped[hour].append(entry)

    return dict(grouped)

def find_peak_error_hour(entries):
    """Find hour with most errors."""
    # Step 1: Filter errors only
    errors = [e for e in entries if e.get('level') == 'ERROR']

    if not errors:
        return (None, 0)

    # Step 2: Count errors per hour
    hour_counts = defaultdict(int)
    for entry in errors:
        dt = parse_timestamp(entry['timestamp'])
        if dt:
            hour_counts[dt.hour] += 1

    # Step 3: Find hour with max errors
    peak_hour = max(hour_counts, key=hour_counts.get)
    return (peak_hour, hour_counts[peak_hour])

def filter_by_date_range(entries, start_date, end_date):
    """Filter entries by date range."""
    filtered = []
    for entry in entries:
        dt = parse_timestamp(entry['timestamp'])
        if dt and start_date <= dt <= end_date:
            filtered.append(entry)
    return filtered

def filter_by_level(entries, level):
    """Filter entries by log level."""
    return [e for e in entries if e.get('level') == level.upper()]

def calculate_error_rate(entries):
    """Calculate error rate percentage."""
    if not entries:
        return 0.0
    error_count = sum(1 for e in entries if e.get('level') == 'ERROR')
    return (error_count / len(entries)) * 100

# Test the functions
test_entries = [
    {'timestamp': '2024-01-15 09:00:00', 'level': 'INFO', 'message': 'Start'},
    {'timestamp': '2024-01-15 09:30:00', 'level': 'ERROR', 'message': 'Failed'},
    {'timestamp': '2024-01-15 10:00:00', 'level': 'ERROR', 'message': 'Timeout'},
    {'timestamp': '2024-01-15 10:15:00', 'level': 'ERROR', 'message': 'Connection'},
    {'timestamp': '2024-01-15 11:00:00', 'level': 'INFO', 'message': 'OK'},
]

print("=== Advanced Log Analysis ===")

print("\n1. Group by hour:")
for hour, items in group_by_hour(test_entries).items():
    print(f"  Hour {hour}:00 - {len(items)} entries")

print("\n2. Peak error hour:")
hour, count = find_peak_error_hour(test_entries)
print(f"  Hour {hour}:00 with {count} errors")

print("\n3. Error rate:")
rate = calculate_error_rate(test_entries)
print(f"  {rate:.1f}% of entries are errors")

print("\n4. Filter by level (ERROR):")
errors = filter_by_level(test_entries, 'ERROR')
for e in errors:
    print(f"  {e['timestamp']}: {e['message']}")

