from fastapi import FastAPI, Depends, HTTPException
from pydantic import BaseModel
import sqlite3
from datetime import datetime

# FastAPI Notes API with SQLite
# This solution demonstrates database-backed REST API with FastAPI

app = FastAPI()
DATABASE = 'notes.db'

# Pydantic model for request validation
class NoteCreate(BaseModel):
    title: str
    content: str

def get_db():
    """Database dependency - yields connection, auto-closes."""
    conn = sqlite3.connect(DATABASE)
    conn.row_factory = sqlite3.Row
    try:
        yield conn
    finally:
        conn.close()

def init_db():
    """Initialize the database with notes table."""
    with sqlite3.connect(DATABASE) as conn:
        conn.execute('''
            CREATE TABLE IF NOT EXISTS notes (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                title TEXT NOT NULL,
                content TEXT NOT NULL,
                created_at TEXT DEFAULT CURRENT_TIMESTAMP
            )
        ''')
        conn.commit()

def row_to_dict(row):
    """Convert sqlite3.Row to dictionary."""
    return dict(row) if row else None

@app.get('/api/notes')
def get_all_notes(db = Depends(get_db)):
    """Get all notes."""
    cursor = db.execute('SELECT * FROM notes ORDER BY created_at DESC')
    notes = [row_to_dict(row) for row in cursor.fetchall()]
    return {'notes': notes, 'count': len(notes)}

@app.get('/api/notes/{note_id}')
def get_note(note_id: int, db = Depends(get_db)):
    """Get a specific note."""
    cursor = db.execute('SELECT * FROM notes WHERE id = ?', (note_id,))
    note = row_to_dict(cursor.fetchone())
    if not note:
        raise HTTPException(status_code=404, detail='Note not found')
    return note

@app.post('/api/notes', status_code=201)
def create_note(note: NoteCreate, db = Depends(get_db)):
    """Create a new note - Pydantic validates automatically."""
    cursor = db.execute(
        'INSERT INTO notes (title, content) VALUES (?, ?)',
        (note.title, note.content)
    )
    db.commit()
    return {'id': cursor.lastrowid, 'message': 'Note created'}

@app.delete('/api/notes/{note_id}')
def delete_note(note_id: int, db = Depends(get_db)):
    """Delete a note."""
    cursor = db.execute('DELETE FROM notes WHERE id = ?', (note_id,))
    db.commit()
    if cursor.rowcount == 0:
        raise HTTPException(status_code=404, detail='Note not found')
    return {'message': f'Note {note_id} deleted'}

if __name__ == '__main__':
    init_db()
    print('Notes API - Run with: uvicorn main:app --reload')
    print('API docs at: http://localhost:8000/docs')

