import asyncio
from dataclasses import dataclass

@dataclass
class EmailResult:
    email: str
    success: bool
    error: Exception | None = None

class EmailError(Exception):
    def __init__(self, email: str, reason: str):
        self.email = email
        super().__init__(f"{email}: {reason}")

async def send_email(email: str) -> EmailResult:
    await asyncio.sleep(0.1)
    # Simulate some failures
    if "2" in email:
        raise EmailError(email, "Mailbox full")
    if "4" in email:
        raise EmailError(email, "Invalid address")
    return EmailResult(email=email, success=True)

async def send_bulk_emails(emails: list[str]) -> tuple[list[str], list[EmailError]]:
    successes: list[str] = []
    failures: list[EmailError] = []

    async with asyncio.TaskGroup() as tg:
        tasks = {email: tg.create_task(send_email(email)) for email in emails}

    # This won't be reached if ANY task fails - we need a different approach
    # Let's wrap each task to catch individual failures:

async def send_bulk_emails_safe(emails: list[str]):
    """Send emails with partial failure handling."""
    successes = []
    failures = []

    async def safe_send(email: str):
        try:
            await send_email(email)
            return ("success", email)
        except EmailError as e:
            return ("failure", e)

    async with asyncio.TaskGroup() as tg:
        tasks = [tg.create_task(safe_send(email)) for email in emails]

    for task in tasks:
        status, result = task.result()
        if status == "success":
            successes.append(result)
        else:
            failures.append(result)

    print(f"Successfully sent to: {successes}")
    if failures:
        print(f"Failed to send to {len(failures)} recipients:")
        for err in failures:
            print(f"  - {err}")

    return successes, failures

asyncio.run(send_bulk_emails_safe([
    "user1@email.com",
    "user2@email.com",
    "user3@email.com",
    "user4@email.com"
]))

